import { CommonModule } from '@angular/common';
import { Component, TemplateRef, ViewEncapsulation, computed, input, model } from '@angular/core';
import { uniqueId } from '../../utils/random';
import { IconKeyboardArrowDown } from '../icons/keyboard-arrow-down';
import { IconKeyboardArrowUp } from '../icons/keyboard-arrow-up';

export interface AccordionSection {
    /**
     * The content of the accordion.
     *
     * @required
     */
    children?: TemplateRef<any> | string;
    /**
     * The title of the accordion.
     *
     * @required
     */
    title: string;
    /** The subtitle of the accordion. */
    subtitle?: string;
    /** The leading element to display in the accordion header. */

    leading?: TemplateRef<any> | string;
    /** The trailing element to display in the accordion header. */

    trailing?: TemplateRef<any> | string;
    /**
     * If the accordion is initially open.
     *
     * This is ignored if the accordion section disabled property is true.
     *
     * @default false
     */
    isOpen?: boolean;
    /**
     * Indicates whether the accordion is disabled.
     *
     * @default false
     */
    disabled?: boolean;
    /**
     * The unique identifier for the accordion item.
     *
     * If not provided it will be generated automatically.
     */
    id?: string;
}

/**
 * A vertical stack of collapsible panels or that allows customers to expand or collapse each panel individually to
 * reveal or hide their content.
 *
 * @example
 *     ```html
 *     <ui-accordion [singleOpen]="false" [items]="[{title: 'Foo', children: 'Foo Children'}, {title: 'Bar', children: 'Bar Children'}, {title: 'Car', children: 'Car Children'}]"></ui-accordion>
 *     ```;
 *
 * @name Accordion
 * @phase Dev
 */
@Component({
    selector: 'ui-accordion',
    standalone: true,
    imports: [CommonModule, IconKeyboardArrowDown, IconKeyboardArrowUp],
    template: `@for (item of itemsWithIds(); track item) {
        <section data-bspk="accordion-item" [attr.data-disabled]="item.disabled ? true : null" [id]="item.id">
            <button
                type="button"
                [attr.aria-controls]="item.id + '-content'"
                [attr.aria-expanded]="isOpen(item)"
                data-header
                [disabled]="item.disabled ? true : null"
                (click)="!item.disabled && toggleOpen(item.id!)">
                @if (item.leading) {
                    <span data-leading>
                        @if (isTemplateRef(item.leading)) {
                            <ng-container>
                                <ng-container *ngTemplateOutlet="item.leading as TemplateRef"></ng-container>
                            </ng-container>
                        } @else {
                            <ng-container
                                *ngTemplateOutlet="leadingText; context: { $implicit: item.children }"></ng-container>
                        }
                        <ng-template #leadingText>{{ item.leading }}</ng-template>
                    </span>
                }

                <span data-title-subtitle>
                    <span data-title>{{ item.title }}</span>

                    @if (item.subtitle) {
                        <span data-subtitle>{{ item.subtitle }}</span>
                    }
                </span>

                @if (item.trailing) {
                    <span data-trailing>
                        @if (isTemplateRef(item.trailing)) {
                            <ng-container>
                                <ng-container *ngTemplateOutlet="item.trailing as TemplateRef"></ng-container>
                            </ng-container>
                        } @else {
                            <ng-container
                                *ngTemplateOutlet="trailingText; context: { $implicit: item.children }"></ng-container>
                        }
                        <ng-template #trailingText>{{ item.trailing }}</ng-template>
                    </span>
                }

                <span data-arrow>
                    @if (isOpen(item)) {
                        <icon-keyboard-arrow-up />
                    } @else {
                        <icon-keyboard-arrow-down />
                    }
                </span>
            </button>

            @if (isOpen(item)) {
                <div data-content [attr.data-hidden]="!isOpen(item) ? true : null" [id]="item.id + '-content'">
                    @if (isTemplateRef(item.children)) {
                        <ng-container>
                            <ng-container *ngTemplateOutlet="item.children as TemplateRef"></ng-container>
                        </ng-container>
                    } @else {
                        <ng-container
                            *ngTemplateOutlet="childrenText; context: { $implicit: item.children }"></ng-container>
                    }
                    <ng-template #childrenText let-content>{{ content }}</ng-template>
                </div>
            }
            <span data-divider></span>
        </section>
    }`,
    styleUrls: ['./accordion.scss'],
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'accordion',
    },
})
export class UIAccordion {
    /**
     * Array of accordion sections
     *
     * @exampleType Array<AccordionSection>
     * @required
     */
    readonly items = input<AccordionSection[]>([]);

    /**
     * If true only one accordion section can be opened at a time
     *
     * @default true
     */
    readonly singleOpen = input(true);

    readonly itemsWithIds = computed(() =>
        this.items().map((item): AccordionSection & { id: string } => ({
            ...item,
            id: item.id || uniqueId('accordion-item'),
        })),
    );

    readonly openSections = model(
        this.itemsWithIds()
            .filter((i) => i.isOpen)
            .map((i) => i.id!),
    );

    toggleOpen(itemId: string) {
        const isOpen = this.openSections().includes(itemId);

        if (this.singleOpen()) {
            this.openSections.set(isOpen ? [] : [itemId]);
            return;
        }

        this.openSections.set(
            isOpen ? this.openSections().filter((id) => id !== itemId) : [...this.openSections(), itemId],
        );
    }

    isOpen(item: AccordionSection & { id: string }): boolean {
        return !!item.id && this.openSections().includes(item.id) && !item.disabled;
    }

    isTemplateRef(value: any): value is TemplateRef<any> {
        return value instanceof TemplateRef;
    }
}

import { Component, ElementRef, ViewEncapsulation, input, viewChild } from '@angular/core';

export interface BadgeProps {
    count?: number;
    size?: string;
    color?: string;
    surfaceBorder?: boolean;
}

/**
 * Visual indicator for new items within a parent page represented with a numerical count of new items.
 *
 * @example
 *     import { Badge } from '@bspk/ui/Badge';
 *
 *     <Badge count={5} size="small" color="primary" surfaceBorder={true} />;
 *
 * @exampleDescription This example shows a badge with a count of 5, size small, and primary variant.
 *
 * @name Badge
 * @phase Dev
 */

@Component({
    selector: 'ui-badge',
    standalone: true,
    template: `<span #contentWrapper><ng-content></ng-content></span>
        <sup
            data-bspk="badge"
            [attr.data-color]="color()"
            [attr.data-size]="size()"
            [attr.data-surface-border]="surfaceBorder() ? true : undefined"
            [attr.data-attachment]="hasProjectedContent ? true : undefined">
            {{ typeof count() === 'number' && count()! > 99 ? '99+' : count() }}
        </sup>`,
    styleUrls: ['./badge.scss'],
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'badge-wrapper',
        'data-attachment-wrapper': '',
    },
})
export class UIBadge {
    /**
     * The context for which the badge is applied.
     *
     * Could be a button, link, or any other element that the badge is associated with.
     */
    readonly contentWrapper = viewChild.required<ElementRef<HTMLElement>>('contentWrapper');

    /**
     * The content of the badge. If larger than 99, the badge will display '99+'.
     *
     * @example
     *     5;
     *
     * @default 1
     */
    readonly count = input<number | undefined>(1);

    /**
     * The size of the badge.
     *
     * @default small
     */
    readonly size = input<'small' | 'x-small'>('small');

    /**
     * The color variant of the badge.
     *
     * @default primary
     */
    readonly color = input<'primary' | 'secondary'>('primary');

    /**
     * Whether the badge should have a border that matches the surface color.
     *
     * @default false
     */
    readonly surfaceBorder = input(false);

    /**
     * Indicates whether the badge has projected (custom) content.
     *
     * This is set to `true` if any content is projected into the badge component (for example,
     * <ui-badge>New</ui-badge>), and `false` if only the `count` input is used. Used internally to determine whether to
     * display the projected content or the count.
     *
     * @internal
     */
    get hasProjectedContent() {
        return !!this.contentWrapper().nativeElement?.childNodes.length;
    }
}

import { Component, EventEmitter, Output, ViewEncapsulation, input } from '@angular/core';
import { AlertVariant, AsInputSignal } from '../../types/common';
import { UIButton } from '../button';
import { IconCheckCircleFill, IconClose, IconErrorFill, IconInfoFill, IconWarningFill } from '../icons';

export interface BannerAlertProps {
    /**
     * The color variant of the banner alert.
     *
     * @default informational
     */
    variant?: AlertVariant;
    /**
     * The header of the banner alert.
     *
     * @required
     */
    header: string;
    /**
     * The body of the banner alert.
     *
     * @type multiline
     * @required
     */
    body: string;
    /**
     * This property may be undefined or an object containing required CallToActionButton properties.
     *
     * @type CallToActionButton
     */
    callToAction?: CallToActionButton;
    /**
     * Is the alert elevated. If true a drop shadow is added.
     *
     * @default false
     */
    elevated?: boolean;
}

export interface CallToActionButton {
    /**
     * The label of the call to action button.
     *
     * @required
     */
    label: string;
    /**
     * The callback function for the call to action button.
     *
     * @required
     */
    onClick: () => void;
}

@Component({
    selector: 'ui-banner-alert',
    imports: [UIButton, IconErrorFill, IconInfoFill, IconCheckCircleFill, IconWarningFill],
    template: `<div data-icon-bar>
            @if (variant() === 'error') {
                <icon-error-fill />
            } @else if (variant() === 'informational') {
                <icon-info-fill />
            } @else if (variant() === 'success') {
                <icon-check-circle-fill />
            } @else if (variant() === 'warning') {
                <icon-warning-fill />
            }
        </div>
        <div data-content>
            @if (header() || onClose.observed) {
                <div data-header>
                    <span>{{ header() }}</span>
                    @if (onClose.observed) {
                        <ui-button
                            (click)="emitClose()"
                            label="Close"
                            [icon]="IconClose"
                            [iconOnly]="true"
                            [variant]="'tertiary'"
                            [size]="'small'" />
                    }
                </div>
            }
            <div data-body>
                <span>{{ body() }}</span>
                @if (callToAction()) {
                    <ui-button
                        [label]="callToAction()!.label"
                        (click)="callToAction()!.onClick()"
                        [size]="'small'"
                        [variant]="'tertiary'" />
                }
            </div>
        </div>`,
    styleUrl: './banner-alert.scss',
    standalone: true,
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'banner-alert',
        '[attr.data-elevated]': 'elevated() || undefined',
        '[attr.data-variant]': 'variant()',
        role: 'alert',
    },
})
export class UIBannerAlert implements AsInputSignal<BannerAlertProps> {
    /** Function to call when the banner alert is closed. */
    @Output() onClose = new EventEmitter<void>();

    readonly variant = input<BannerAlertProps['variant']>('informational');
    readonly elevated = input<BannerAlertProps['elevated']>(false);
    readonly header = input.required<BannerAlertProps['header']>();
    readonly body = input.required<BannerAlertProps['body']>();
    readonly callToAction = input<BannerAlertProps['callToAction']>();

    IconClose = IconClose;

    emitClose() {
        this.onClose?.emit();
    }
}

import { ListItemProps } from '../list-item/list-item';

export interface BreadcrumbItem extends ListItemProps {
    /**
     * The label of the breadcrumb item.
     *
     * @example
     *     'Page 1';
     *
     * @required
     */
    label: string;
    /**
     * The href of the breadcrumb item.
     *
     * @example
     *     'https://bspk.anywhere.re';
     *
     * @required
     */
    href: string;
}

import { InputSignal, Signal } from '@angular/core';

export type AsInputSignal<T> = {
    [K in keyof T]: undefined extends T[K] ? InputSignal<T[K] | undefined> : InputSignal<NonNullable<T[K]>>;
};

export type AsSignal<T> = {
    [K in keyof T]: undefined extends T[K] ? Signal<T[K] | undefined> : Signal<NonNullable<T[K]>>;
};

export type AlertVariant = 'error' | 'informational' | 'success' | 'warning';

/** Sets a ref to the given element. */
export type SetRef<T> = (instance: T | null) => void;

export type DataProps = Record<`data-${string}`, string>;

export type ButtonSize = 'large' | 'medium' | 'small' | 'x-small';

export interface CallToActionButton {
    /**
     * The label of the call to action button.
     *
     * @required
     */
    label: string;
    /**
     * The callback function for the call to action button.
     *
     * @required
     */
    onClick: () => void;
    /** The size of the call to action button. */
    size?: ButtonSize;
}

export interface CommonPropsLibrary {
    /**
     * Indicates that the element is in an invalid state and displays the error theme.
     *
     * If set to true, an accompanying error message should be provided.
     *
     * @default false
     */
    invalid?: boolean;
    /** The id of the element. If not provided one will be generated. */
    id?: string;
    /**
     * Marks the element as active and displays active state theme.
     *
     * @default false
     */
    active?: boolean;
    /**
     * The size of the element.
     *
     * @default medium
     */
    size?: 'large' | 'medium' | 'small';
    /**
     * Determines if the element is [required](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/required).
     *
     * @default false
     */
    required?: boolean;
    /**
     * Determines if the element is [disabled](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/disabled).
     *
     * @default false
     */
    disabled?: boolean;
    /**
     * Determines if the element is [readonly](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/readonly).
     *
     * @default false
     */
    readOnly?: boolean;
    /**
     * The [name](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/input#name) of the control.
     *
     * @required
     */
    name: string;
    /**
     * The value of the control.
     *
     * @required
     */
    optionValue?: string;
    /**
     * The aria-label for the element.
     *
     * This is used to provide an accessible name for the element when a visible label is not present.
     *
     * Ensure this is provided when using the element in isolation to maintain accessibility.
     */
    ariaLabel?: string;
    /** Identifies the parent component. Helps with styling, debugging, and/or testing purposes. */
    owner?: string;
    /**
     * The ARIA role of the element.
     *
     * @type string
     */
    ariaRole?: AriaRole;
    /**
     * Inline styles to apply to the element.
     *
     * Allows for CSS variables to be passed in as well.
     */
    style?: string;
    /*
     * The aria-describedby attribute for the control.
     */
    ariaDescribedBy?: string;
    /*
     * The aria-errormessage attribute for the control.
     */
    ariaErrorMessage?: string;
    /** The aria-labelledby attribute for the control. */
    ariaLabelledBy?: string;
    /**
     * Indicates the current "selected" state of the list item when used in a selectable context, such as within a
     * ListItemMenu.
     */
    ariaSelected?: boolean;
    /** The aria-disabled attribute for the element. */
    ariaDisabled?: boolean;
    /** The aria-readonly attribute for the element. */
    ariaReadonly?: boolean;
    /** The htmlFor attribute for the element. */
    htmlFor?: string;
    /** Explicit tabIndex; defaults to 0 when actionable, otherwise -1. */
    tabIndex?: number;
}

export type CommonProps<K extends keyof CommonPropsLibrary> = Pick<CommonPropsLibrary, K>;

export type RequiredCommonProps<K extends keyof CommonPropsLibrary> = Required<Pick<CommonPropsLibrary, K>>;

export type FieldControlProps<ValueType = string> = CommonProps<
    | 'ariaDescribedBy'
    | 'ariaErrorMessage'
    | 'ariaLabel'
    | 'ariaLabelledBy'
    | 'disabled'
    | 'id'
    | 'invalid'
    | 'name'
    | 'readOnly'
    | 'required'
> & {
    /**
     * The value of the field control.
     *
     * @required
     */
    value: ValueType | undefined;
};

