}

/**
 * Dynamically generated options that are suggested to the user as responses or prompts.
 *
 * @example
 *     import { Chip } from '@bspk/ui/Chip';
 *
 *     <Chip label="Label" click={() => sendSnackbar('Chip clicked!')}>
 *     Example Chip
 *     </Chip>;
