import { Renderer2, signal } from '@angular/core';
import {
    arrow,
    computePosition,
    ComputePositionReturn,
    flip,
    MiddlewareState,
    offset,
    OffsetOptions,
    Placement,
    size,
    Strategy,
} from '@floating-ui/dom';
import { UtilityBase } from '../../types/common';

export interface FloatingProps {
    /**
     * The preferred placement of the floating element.
     *
     * This determines where the floating element will be positioned relative to the reference element.
     *
     * @default bottom-start
     */
    placement?: Placement;
    /**
     * The positioning strategy ('absolute' or 'fixed').
     *
     * When set to 'fixed', the floating element will be positioned relative to the viewport.
     *
     * When set to 'absolute', the floating element will be positioned relative to the nearest positioned ancestor.
     *
     * @default fixed
     */
    strategy?: Strategy;
    /**
     * The offset options for the floating element.
     *
     * @default 0
     */
    offsetOptions?: OffsetOptions;
    /**
     * When set to true, the width of the floating element will match the width of the reference element.
     *
     * @default false
     */
    refWidth?: boolean;
    /** The reference element for positioning. */
    reference?: HTMLElement | null;
    /** The floating element to be positioned. */
    floating?: HTMLElement | null;
    /** The arrow element for the floating element. */
    arrow?: HTMLElement | null;
}

/**
 * A utility to position an element relative to a reference element using floating UI logic.
 *
 * Should be used in components that require floating element positioning.
 */
export class FloatingUtility implements UtilityBase<FloatingProps> {
    readonly props = signal<FloatingProps>({});

    constructor(private render: Renderer2) {}
    destroy(): void {
        // No-op
    }

    updateProps(props: Partial<FloatingProps>) {
        this.props.set({
            ...this.props(),
            ...props,
        });
    }

    init(
        props: Omit<FloatingProps, 'floating' | 'reference'> & Required<Pick<FloatingProps, 'floating' | 'reference'>>,
    ) {
        this.updateProps(props);
    }

    async compute(nextProps?: Partial<FloatingProps>) {
        const {
            placement,
            strategy,
            offsetOptions,
            refWidth,
            floating,
            reference,
            arrow: arrowEl,
        } = {
            ...this.props(),
            ...nextProps,
        };

        if (
            // Missing required elements
            !floating ||
            !reference
        )
            return {} as ComputePositionReturn;

        this.render.setStyle(floating, 'position', strategy || 'fixed');
        this.render.setStyle(floating, 'opacity', '0');
        this.render.setStyle(floating, 'pointerEvents', 'none');

        const returnValue = await computePosition(reference, floating, {
            placement: placement || 'bottom-start',
            strategy: strategy || 'fixed',
            middleware: [
                arrowEl ? arrow({ element: arrowEl! }) : undefined,
                offset(offsetOptions),
                flip(),
                refWidth &&
                    size({
                        apply({ rects, elements }: MiddlewareState) {
                            Object.assign(elements.floating.style, {
                                width: `${rects.reference.width}px`,
                            });
                        },
                    }),
            ],
        });

        const {
            x,
            y,
            middlewareData: { arrow: arrowCoords },
        } = returnValue;

        this.render.setStyle(floating, 'left', `${x}px`);
        this.render.setStyle(floating, 'top', `${y}px`);

        setTimeout(() => {
            this.render.setStyle(floating, 'opacity', '1');
            this.render.setStyle(floating, 'pointerEvents', 'auto');
        }, 10);

        if (arrowEl) {
            if (arrowCoords) {
                this.render.setStyle(arrowEl, 'left', `${arrowCoords?.x}px`);
                this.render.setStyle(arrowEl, 'top', `${arrowCoords?.y}px`);
            }
        }

        return returnValue;
    }
}

import { computed, effect, signal } from '@angular/core';
import { UtilityBase } from '../../types/common';
import { KeyboardEventCode } from '../../utils/keyboard';
import { keydownHandler, KeysCallback } from '../../utils/keydown-handler';

export type ArrowKeyNames = Extract<KeyboardEventCode, `Arrow${string}`>;

const ARROW_KEYS: ArrowKeyNames[] = ['ArrowLeft', 'ArrowRight', 'ArrowUp', 'ArrowDown'];

const DEFAULT_INCREMENTS: Record<ArrowKeyNames, number> = {
    ArrowLeft: -1,
    ArrowRight: 1,
    ArrowUp: -1,
    ArrowDown: 1,
};

export interface KeyNavigationUtilityProps {
    /**
     * An array of string IDs representing the navigable elements. These IDs should correspond to the `id` attributes of
     * the elements in the DOM. Ensure the elements are not disabled.
     */
    ids?: string[];
    /**
     * An optional callback function that is invoked when an arrow key is pressed. This function receives the key name,
     * the keyboard event, and the next active element ID. If the function returns `true`, the default navigation
     * behavior is prevented; if it returns `false` or is not provided, the default behavior proceeds.
     */
    callback?: (params: ArrowKeyNavigationCallbackParams) => boolean;
    /** Optional configuration to set navigation direction increments. */
    increments?: Record<ArrowKeyNames, number>;
    /**
     * The ID of the element that should be active by default. If not provided, the first ID in the `ids` array will be
     * used.
     */
    defaultActiveId?: string;
    /** The ID of the currently active element. */
    activeElementId: string | null;
}

export type ArrowKeyNavigationCallbackParams = Pick<KeyNavigationUtilityProps, 'activeElementId'> & {
    increment: number;
    key: ArrowKeyNames;
    event: KeyboardEvent;
};

/**
 * A hook to manage arrow key navigation for a list of elements.
 *
 * Should be used in components that require keyboard navigation via arrow keys.
 */
export class KeyNavigationUtility implements UtilityBase<KeyNavigationUtilityProps> {
    readonly props = signal<KeyNavigationUtilityProps>({
        ids: [],
        activeElementId: null,
        defaultActiveId: undefined,
        increments: DEFAULT_INCREMENTS,
        callback: undefined,
    });

    readonly arrowKeyCallbacks = computed(() => {
        return Object.fromEntries(
            ARROW_KEYS.map((key) => [
                key,
                (event: KeyboardEvent) => {
                    const { ids, increments, callback, activeElementId } = this.props();

                    if (!ids || !increments) return;

                    let nextIndex = 0;
                    let currentIndex = 0;
                    let nextId = ids[nextIndex];
                    const increment = increments[key];

                    if (activeElementId) {
                        currentIndex = ids.indexOf(activeElementId);
                        nextIndex = (currentIndex + increment + ids.length) % ids.length;
                        nextId = ids[nextIndex];
                    }

                    if (
                        typeof callback === 'function' &&
                        callback({ key, event, activeElementId: nextId, increment }) === false
                    )
                        return;
                    event.preventDefault();
                    this.setActiveElementId(nextId);
                },
            ]),
        ) as KeysCallback;
    });

    constructor() {
        effect(() => {
            const { ids, activeElementId } = this.props();

            // If the active element is not in the list, reset the first ID as active
            if (ids && activeElementId && !ids.includes(activeElementId)) {
                this.setActiveElementId(ids[0] || null);
            }
        });
    }

    get activeElementId() {
        return this.props().activeElementId;
    }

    get activeElement(): HTMLElement | null {
        return (this.activeElementId && document.querySelector(`[id="${this.activeElementId}"]`)) || null;
    }

    /**
     * Cleans up the utility by resetting its properties.
     *
     * Should be called in ngOnDestroy.
     */
    destroy() {
        this.props.set({
            ids: [],
            activeElementId: null,
            defaultActiveId: undefined,
            callback: undefined,
        });
    }

    setActiveElementId(id: string | null) {
        this.updateProps({ activeElementId: id });

        if (id)
            document.querySelector(`[id="${id}"]`)?.scrollIntoView({
                block: 'nearest',
                behavior: 'smooth',
                inline: 'nearest',
            });
    }

    updateProps(props: Partial<KeyNavigationUtilityProps>) {
        this.props.set({
            ...this.props(),
            ...props,
        });
    }

    /**
     * Initializes the key navigation utility with the provided properties.
     *
     * Should be called in ngAfterViewInit.
     */
    init(props: Partial<KeyNavigationUtilityProps>) {
        this.updateProps(props);
    }

    /**
     * Handles keyboard events for arrow key navigation.
     *
     * Should be called within a keyboard event listener.
     *
     * Additional callbacks for specific keys can be provided via the `callbacks` parameter.
     */
    handleKeydown(event: KeyboardEvent, callbacks?: KeysCallback) {
        keydownHandler([this.arrowKeyCallbacks(), callbacks || {}], {
            preventDefault: true,
            stopPropagation: true,
        })(event);
    }
}

import {
    Directive,
    ElementRef,
    Renderer2,
    inject,
    input,
    OnDestroy,
    effect,
    EffectRef,
    ViewContainerRef,
    EnvironmentInjector,
} from '@angular/core';
import { AsInputSignal } from '../../types/common';

export interface LinkProps {
    /** The variant of the link. Controls the icon that is displayed and link target. */
    trailingIcon?: 'chevron' | 'external' | 'link';
    /**
     * The href of the link.
     *
     * @example
     *     https://bspk.dev
     */
    href: string | null | undefined;
    /**
     * The size of the link.
     *
     * @default base
     */
    size?: 'base' | 'large' | 'small';
    /**
     * Change the color of the link to a subtle color. This is useful for links that are not primary actions, for
     * example footer menus.
     *
     * @default default
     */
    variant?: 'default' | 'subtle-inverse' | 'subtle';
    /**
     * The target of the link. If the `trailingIcon` is set to `external`, this will default to `_blank`.
     *
     * @default _self
     */
    target?: '_blank' | '_parent' | '_self' | '_top';
    /** Determines if the element is disabled. */
    disabled?: boolean;
}

/**
 * This is the standalone link directive. Apply to an `a` element.
 *
 * @example
 *     <a ui-link href="https://anywhere.re" [label]="'Example label'" trailingIcon="external"></a>
 *
 * @name Link
 * @phase Stable
 */
@Directive({
    selector: 'a[ui-link]',
})
export class UILinkDirective implements AsInputSignal<LinkProps>, OnDestroy {
    readonly trailingIcon = input<LinkProps['trailingIcon']>();
    readonly href = input<LinkProps['href']>();
    readonly size = input<LinkProps['size']>('base');
    readonly variant = input<LinkProps['variant']>('default');
    readonly target = input<LinkProps['target']>('_self');
    readonly disabled = input<LinkProps['disabled']>(false);

    private readonly host = inject<ElementRef<HTMLAnchorElement>>(ElementRef);
    private readonly renderer = inject(Renderer2);
    private env = inject(EnvironmentInjector);

    private labelSpan: HTMLSpanElement | null = null;
    private teardown: EffectRef | null = null;

    constructor(private viewContainerRef: ViewContainerRef) {
        this.teardown = effect(async () => {
            const el = this.host.nativeElement;

            // Set base attributes
            this.renderer.setAttribute(el, 'data-bspk', 'link');
            this.renderer.setAttribute(el, 'data-size', this.size() || 'base');
            this.renderer.setAttribute(el, 'data-trailing-icon', this.trailingIcon() || '');

            // Subtle variants
            const v = this.variant();
            if (v === 'subtle') {
                this.renderer.setAttribute(el, 'data-subtle', 'true');
                this.renderer.removeAttribute(el, 'data-subtle-inverse');
            } else if (v === 'subtle-inverse') {
                this.renderer.setAttribute(el, 'data-subtle-inverse', 'true');
                this.renderer.removeAttribute(el, 'data-subtle');
            } else {
                this.renderer.removeAttribute(el, 'data-subtle');
                this.renderer.removeAttribute(el, 'data-subtle-inverse');
            }

            // href, disabled, target
            const hrefVal = this.href();
            if (hrefVal) this.renderer.setAttribute(el, 'href', hrefVal);
            else this.renderer.removeAttribute(el, 'href');

            const disabledVal = this.disabled();
            if (disabledVal) this.renderer.setAttribute(el, 'disabled', 'true');
            else this.renderer.removeAttribute(el, 'disabled');

            const tIcon = this.trailingIcon();
            const tgt = tIcon === 'external' ? '_blank' : this.target() || '_self';
            this.renderer.setAttribute(el, 'target', tgt);

            // wrap text content in span
            if (!this.labelSpan) {
                const span = this.renderer.createElement('span') as HTMLSpanElement;
                span.textContent = el.textContent;
                this.renderer.setProperty(el, 'textContent', '');
                this.renderer.appendChild(el, span);
                this.labelSpan = span;
            }

            const iconComponent = await this.iconComponent();
            if (iconComponent) {
                const icon = this.viewContainerRef.createComponent(iconComponent);
                this.renderer.appendChild(el, icon.location.nativeElement);
            }
        });
    }

    iconComponent() {
        const tIcon = this.trailingIcon();
        if (tIcon === 'external') return import('../icons/open-in-new').then((m) => m.IconOpenInNew);
        if (tIcon === 'chevron') return import('../icons/chevron-right').then((m) => m.IconChevronRight);
        if (tIcon === 'link') return import('../icons/link').then((m) => m.IconLink);
        return null;
    }

    ngOnDestroy(): void {
        if (this.teardown) this.teardown.destroy();
        this.labelSpan = null;
    }
}

