    handleOpenEvent(event: Event) {
        const props = this.props();

        if (
            !props.label ||
            event.target !== this.referenceEl ||
            props.disabled ||
            // skip if not actively truncated
            (props.truncated && this.referenceEl?.scrollWidth <= this.referenceEl?.clientWidth)
        )
            return;

        if (props.truncated) this.addComponent({ label: this.referenceEl.textContent || '' });

        if (this.tooltipEl) this.renderer.setStyle(this.tooltipEl, 'display', 'block');

        this.floating
            .compute({
                placement: this.props()?.placement || 'top',
                reference: this.referenceEl,
                floating: this.tooltipEl,
                arrow: this.arrowEl,
                offsetOptions: this.arrowEl ? 8 : 4,
                refWidth: false,
            })
            .then(({ placement }) => {
                if (this.tooltipEl && placement !== this.props()?.placement)
                    this.computedPlacement.set(placement as TooltipPlacement);
            });
    }

    handleCloseEvent() {
        const { label, truncated } = this.props();
        if (!label) return;

        if (truncated) this.removeComponent();
        if (this.tooltipEl) this.renderer.setStyle(this.tooltipEl, 'display', 'none');
    }

    ngOnDestroy(): void {
        this.handleCloseEvent();
        if (this.referenceEl) this.renderer.removeAttribute(this.referenceEl, 'aria-labelledby');

        this.removeComponent();
    }

    removeComponent() {
        if (this.tooltipComponent) {
            this.renderer.removeChild(this.tooltipEl.parentNode, this.tooltipEl);
            this.tooltipComponent.destroy();
            this.tooltipComponent = null;
        }
    }

    addComponent(props = this.props()): boolean {
        if (
            // if we can't render or already rendered or disabled or no label we can't show
            !this.document ||
            !this.referenceEl ||
            !props.label
        ) {
            return false;
        }

        this.tooltipComponent = addComponent(this.env, UITooltip, 'ui-tooltip')!;

        if (this.tooltipEl) this.renderer.setStyle(this.tooltipEl, 'display', 'none');
        this.tooltipComponent.instance.id.set(this.tooltipId);
        this.updateTooltipProps(props);
        return true;
    }

    ngOnInit(): void {
        if (!this.referenceEl || !this.props().label) return;

        if (this.props().truncated) {
            this.renderer.setAttribute(this.referenceEl, 'data-truncated', 'true');
        } else {
            this.renderer.setAttribute(this.referenceEl, 'aria-labelledby', this.tooltipId);
            this.addComponent(this.props());
        }
    }
}

/** Single use component to display tooltip content. */
@Component({
    selector: 'ui-tooltip',
    standalone: true,
    template: `
        <span data-text>{{ props().label }}</span>
        @if (props().showTail !== false) {
            <span aria-hidden="true" data-arrow #arrow></span>
        }
    `,
    styleUrl: './tooltip.scss',
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'tooltip',
        role: 'tooltip',
        '[attr.data-placement]': 'props().placement || "top"',
        '[attr.id]': 'id() || null',
    },
})
export class UITooltip {
    readonly arrow = viewChild<ElementRef>('arrow');

    /** Tooltip id for a11y labelling */
    readonly id = signal<string | undefined>(undefined);

    readonly props = model<TooltipProps>({});

    get arrowElement(): HTMLElement | null {
        return this.arrow()?.nativeElement || null;
    }
}

import { CommonModule } from '@angular/common';
import {
    Component,
    Output,
    EventEmitter,
    ViewEncapsulation,
    input,
    AfterViewInit,
    Renderer2,
    inject,
    EnvironmentInjector,
    ElementRef,
} from '@angular/core';
import { IconPerson } from '../icons/person';
import { TooltipUtility } from '../tooltip/tooltip';

export interface UIAvatarProps {
    id: string;
    name: string;
    size?: SizeVariant;
    color?: string;
    initials?: string;
    showIcon?: boolean;
    image?: string;
    src?: string; // For image source
    hideTooltip?: boolean;
    disabled?: boolean;
}

export type SizeVariant =
    | 'large'
    | 'medium'
    | 'small'
    | 'x-large'
    | 'x-small'
    | 'xx-large'
    | 'xxx-large'
    | 'xxxx-large'
    | 'xxxxx-large';

/**
 * An avatar is a visual representation of a user or entity. It can be used to display an initials, icon, or image.
 *
 * @example
 *     <Avatar
 *     color="blue"
 *     showIcon
 *     image="/avatar-01.png"
 *     initials="AR"
 *     name="Andre Giant"
 *     size="large"
 *     disabled={false}
 *     onClick={() => action('Launch avatar popover')}
 *     showIcon={false}
 *     hideTooltip={true}
 *     />;
 *
 * @exampleDescription The image if provided is displayed first, followed by the icon if provided, and finally the initials. If no initials are provided, the first two letters of the name will be used as initials.
 *
 * @name Avatar
 * @phase Dev
 */
@Component({
    selector: 'ui-avatar',
    standalone: true,
    imports: [CommonModule, IconPerson],
    template: ` @if (image()) {
            <img [src]="image()" [alt]="name()" aria-hidden="true" />
        } @else if (showIcon()) {
            <span aria-hidden="true" data-icon>
                <icon-person></icon-person>
            </span>
        } @else if (computedInitials) {
            <span aria-hidden="true" data-initials>
                {{ computedInitials }}
            </span>
        }`,
    styleUrls: ['./avatar.scss'],
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'avatar',
        '[attr.aria-label]': 'name()',
        'aria-roledescription': 'person',
        '[attr.aria-disabled]': 'disabled() || undefined',
        '[attr.tabindex]': '!disabled() && onClick.observed ? 0 : undefined',
        '[attr.role]': "onClick.observed ? 'button' : 'img'",
        '[attr.data-color]': 'color()',
        '[attr.data-size]': 'size()',
        '(click)': 'handleOnClick($event)',
        '(keydown)': 'handleKeyDown($event)',
    },
})
export class UIAvatar implements AfterViewInit {
    /** The function to call when the avatar is clicked. */
    @Output() onClick = new EventEmitter<MouseEvent>();

    /**
     * The name of the person or entity represented by the avatar. This is used for accessibility purposes.
     *
     * @example
     *     Andre Giant
     *
     * @required
     */
    readonly name = input.required<string>();

    /**
     * The size of the avatar.
     *
     * @default small
     */
    readonly size = input<SizeVariant>('small');

    /**
     * The color of the avatar.
     *
     * @default grey
     */
    readonly color = input<string | undefined>('grey');

    /**
     * Customizable initials to display in the avatar limited to 2 characters.
     *
     * By default, initials are the first letters of the first two words in the name. For a single-word name, only one
     * initial is shown. Names with three or more words, only the first two initials are used.
     *
     * @example
     *     AG;
     */
    readonly initials = input<string>();

    /**
     * Whether to show the icon in the avatar instead of the initials.
     *
     * If an image is provided, the image will be shown instead of the icon.
     *
     * @default false
     */
    readonly showIcon = input<boolean | undefined>(false);

    /**
     * The url to the image to display in the avatar.
     *
     * When provided the image will be displayed instead of the icon or initials.
     *
     * @example
     *     /avatar-01.png
     */
    readonly image = input<string | undefined>();

    /**
     * Whether to hide the represented user's name as a tooltip.
     *
     * @default false
     */
    readonly hideTooltip = input<boolean | undefined>(false);

    /**
     * Determines if the element is [disabled](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/disabled).
     *
     * @default false
     */
    readonly disabled = input(false);

    private renderer = inject(Renderer2);
    private env = inject(EnvironmentInjector);
    private host = inject(ElementRef<HTMLElement>);

    private tooltipUtility: TooltipUtility | undefined;

    get computedInitials(): string | undefined {
        const initials = this.initials();
        if (initials) return initials.slice(0, 2).toUpperCase();
        const name = this.name();
        if (name) {
            const parts = name.split(' ').filter(Boolean);
            return parts
                .map((w) => w[0])
                .slice(0, 2)
                .join('')
                .toUpperCase();
        }
        return undefined;
    }

    ngAfterViewInit() {
        if (!this.hideTooltip()) {
            this.tooltipUtility = new TooltipUtility(this.renderer, this.env, {
                label: this.name(),
                reference: this.host.nativeElement,
                placement: 'top',
            });
        }
    }

    onDestroy() {
        this.tooltipUtility?.destroy();
    }

    handleOnClick(event: MouseEvent) {
        if (!this.disabled()) {
            this.onClick.emit(event);
        }
    }

    handleKeyDown(event: KeyboardEvent) {
        if (!this.disabled() && event.key === 'Enter') {
            this.onClick.emit();
        }
    }
}

import { InputSignal, Signal } from '@angular/core';

export type AsInputSignal<T> = {
    [K in keyof T]: undefined extends T[K] ? InputSignal<T[K] | undefined> : InputSignal<NonNullable<T[K]>>;
};

export type AsSignal<T> = {
    [K in keyof T]: undefined extends T[K] ? Signal<T[K] | undefined> : Signal<NonNullable<T[K]>>;
};

export type AlertVariant = 'error' | 'informational' | 'success' | 'warning';

/** Sets a ref to the given element. */
export type SetRef<T> = (instance: T | null) => void;

export type DataProps = Record<`data-${string}`, string>;

export type ButtonSize = 'large' | 'medium' | 'small' | 'x-small';

export interface CallToActionButton {
    /**
     * The label of the call to action button.
     *
     * @required
     */
    label: string;
    /**
     * The callback function for the call to action button.
     *
     * @required
     */
    onClick: () => void;
    /** The size of the call to action button. */
    size?: ButtonSize;
}

export interface CommonPropsLibrary {
    /**
     * Indicates that the element is in an invalid state and displays the error theme.
     *
     * If set to true, an accompanying error message should be provided.
     *
     * @default false
     */
    invalid?: boolean;
    /** The id of the element. If not provided one will be generated. */
    id?: string;
    /**
     * Marks the element as active and displays active state theme.
     *
     * @default false
     */
    active?: boolean;
    /**
     * The size of the element.
     *
     * @default medium
     */
    size?: 'large' | 'medium' | 'small';
    /**
     * Determines if the element is [required](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/required).
     *
     * @default false
     */
    required?: boolean;
    /**
     * Determines if the element is [disabled](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/disabled).
     *
     * @default false
     */
    disabled?: boolean;
    /**
     * Determines if the element is [readonly](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/readonly).
     *
     * @default false
     */
    readOnly?: boolean;
    /**
     * The [name](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/input#name) of the control.
     *
     * @required
     */
    name: string;
    /**
     * The value of the control.
     *
     * @required
     */
    optionValue?: string;
    /**
     * The aria-label for the element.
     *
     * This is used to provide an accessible name for the element when a visible label is not present.
     *
     * Ensure this is provided when using the element in isolation to maintain accessibility.
     */
    ariaLabel?: string;
    /** Identifies the parent component. Helps with styling, debugging, and/or testing purposes. */
    owner?: string;
    /**
     * The ARIA role of the element.
     *
     * @type string
     */
    ariaRole?: AriaRole;
    /**
     * Inline styles to apply to the element.
     *
     * Allows for CSS variables to be passed in as well.
     */
    style?: string;
    /*
     * The aria-describedby attribute for the control.
     */
    ariaDescribedBy?: string;
    /*
     * The aria-errormessage attribute for the control.
     */
    ariaErrorMessage?: string;
    /** The aria-labelledby attribute for the control. */
    ariaLabelledBy?: string;
    /**
     * Indicates the current "selected" state of the list item when used in a selectable context, such as within a
     * ListItemMenu.
     */
    ariaSelected?: boolean;
    /** The aria-disabled attribute for the element. */
    ariaDisabled?: boolean;
    /** The aria-readonly attribute for the element. */
    ariaReadonly?: boolean;
    /** The htmlFor attribute for the element. */
    htmlFor?: string;
    /** Explicit tabIndex; defaults to 0 when actionable, otherwise -1. */
    tabIndex?: number;
}

export type CommonProps<K extends keyof CommonPropsLibrary> = Pick<CommonPropsLibrary, K>;

export type RequiredCommonProps<K extends keyof CommonPropsLibrary> = Required<Pick<CommonPropsLibrary, K>>;

export type FieldControlProps<ValueType = string> = CommonProps<
    | 'ariaDescribedBy'
    | 'ariaErrorMessage'
    | 'ariaLabel'
    | 'ariaLabelledBy'
    | 'disabled'
    | 'id'
