export interface Mask {
    /** The input mask */
    mask: string;

    /** The regex patterns for specific characters */
    patterns: Record<string, { pattern: RegExp; optional?: boolean }>;

    /** Any additional characters that should be considered special */
    specialCharacters?: string[];

    /** Drop special characters from the input value */
    dropSpecialCharacters?: boolean;

    /** For numeric fields, allow negative numbers */
    allowNegative?: boolean;

    /** The mask prefix */
    prefix?: string;

    /** The mask suffix */
    suffix?: string;

    /** The error message to display if invalid */
    errorMessage?: string;
}

/** Mask for a phone number i.e. ###-###-#### */
export const PhoneNumberMask: Mask = {
    mask: '#00-000-0000',
    patterns: {
        '#': { pattern: /[2-9]/ },
        '0': { pattern: /[0-9]/ },
    },
    errorMessage: 'Phone number must be formatted as ###-###-####',
};

/** Different variant using periods instead of dashes i.e. ###.###.#### */
export const PhoneNumber2Mask: Mask = {
    mask: '#00.000.0000',
    patterns: {
        '#': { pattern: /[2-9]/ },
        '0': { pattern: /[0-9]/ },
    },
};

/** Different variant using parentheses around the area code i.e. (###) ###-#### */
export const PhoneNumber3Mask: Mask = {
    mask: '(#00) 000-0000',
    patterns: {
        '#': { pattern: /[2-9]/ },
        '0': { pattern: /[0-9]/ },
    },
};

/** Mask for a zip code i.e. ##### OR #####-#### */
export const ZipCodeMask: Mask = {
    mask: '00000|00000-0000',
    patterns: {},
};

/** Mask for a whole number i.e. #### */
export const WholeNumberMask: Mask = {
    mask: '0*',
    patterns: {
        '0': { pattern: /[0-9]/ },
    },
};

/** Decimal number that supports two decimal places i.e. ###.## */
export const DecimalNumberMask: Mask = Object.freeze({
    mask: '0*.00',
    patterns: {
        '0': { pattern: /^(0|[1-9]d*)(.d+)?$/ },
    },
});

export class Masks {
    static from(mask: Mask, overrides?: Partial<Mask>) {
        return {
            ...mask,
            ...overrides,
        };
    }
}

import { signal } from '@angular/core';
import { UtilityBase } from '../../types/common';

export interface OutsideClickUtilityProps {
    /**
     * The elements to monitor for outside clicks.
     *
     * If used in the directive, this can be left empty and the directive's host element will be used.
     */
    elements?: HTMLElement[];
    /** The callback function to execute on outside click. */
    callback: (event?: Event | KeyboardEvent | MouseEvent) => void;
    /**
     * Whether the outside click detection is disabled.
     *
     * @default false
     */
    disabled?: boolean;
    /**
     * Whether to handle 'Tab' key presses as outside clicks.
     *
     * @default false
     */
    handleTabs?: boolean;
}

/**
 * Utility to detect clicks outside specified elements and execute a callback.
 *
 * Should be used in components that require outside click detection.
 */
export class OutsideClickUtility implements UtilityBase<OutsideClickUtilityProps> {
    readonly props = signal<OutsideClickUtilityProps>({
        elements: [],
        callback: () => {
            /* no-op */
        },
        disabled: false,
        handleTabs: false,
    });

    updateProps(next: Partial<OutsideClickUtilityProps>) {
        this.props.set({
            ...this.props(),
            ...next,
        });
    }

    init(props: OutsideClickUtilityProps) {
        if (typeof document === 'undefined') return;

        this.updateProps(props);

        if (this.props().elements?.length) {
            document.addEventListener('mousedown', this.handleClickOutside);
            document.addEventListener('keydown', this.handleOutsideTab);
        }
    }

    destroy() {
        if (typeof document === 'undefined') return;
        document.removeEventListener('mousedown', this.handleClickOutside);
        document.removeEventListener('keydown', this.handleOutsideTab);
    }

    private handleClickOutside = (event: MouseEvent) => {
        const { elements, callback, disabled } = this.props();
        if (disabled || elements?.some?.((element) => element?.contains?.(event.target as Node))) return;
        callback?.(event);
    };

    private handleOutsideTab = (event: KeyboardEvent) => {
        const { elements, callback, disabled, handleTabs } = this.props();

        if (!handleTabs || event.key !== 'Tab' || disabled) return;

        setTimeout(() => {
            if (elements?.some?.((element) => element?.contains?.(document.activeElement))) return;
            callback?.(event);
        }, 0);
    };
}

