import { Directive, ElementRef, computed, input } from '@angular/core';

export type UIFlexJustify =
    | 'around'
    | 'between'
    | 'center'
    | 'end'
    | 'evenly'
    | 'flex-end'
    | 'flex-start'
    | 'space-around'
    | 'space-between'
    | 'space-evenly'
    | 'start';

export type UIFlexAlign = 'baseline' | 'center' | 'end' | 'flex-end' | 'flex-start' | 'start' | 'stretch';

/**
 * Apply flexbox layout to an element.
 *
 * Usage examples: <div uiFlex></div> <div [uiFlex]="'inline'" direction="column" justify="between" align="center"
 * wrap="wrap" gap="8px"></div>
 */
@Directive({
    selector: '[ui-flex]',
    standalone: true,
    host: {
        '[style]': 'computedStyle()',
    },
})
export class UIFlexDirective {
    readonly computedStyle = computed(() => ({
        display: 'flex',
        flexDirection: this.direction(),
        justifyContent: this.normalizeJustify(this.justify()),
        alignItems: this.normalizeAlign(this.align()),
        flexWrap: this.wrap(),
        gap: this.gap() ?? null,
    }));

    /** Flex-direction */
    readonly direction = input<'column-reverse' | 'column' | 'row-reverse' | 'row'>('row');

    /** Justify-content Accepts CSS values or shorthands: start,end,center,between,around,evenly */
    readonly justify = input<UIFlexJustify>('flex-start');

    /** Align-items Accepts CSS values or shorthands: start,end,center,stretch,baseline */
    readonly align = input<UIFlexAlign>('stretch');

    /** Flex-wrap */
    readonly wrap = input<'nowrap' | 'wrap-reverse' | 'wrap'>('nowrap');

    /** Gap (e.g., 8px, 1rem, var(--space-2)) */
    readonly gap = input<string>();

    constructor(private elementRef: ElementRef) {}

    private normalizeJustify(v: UIFlexJustify): string {
        switch (v) {
            case 'start':
                return 'flex-start';
            case 'end':
                return 'flex-end';
            case 'between':
                return 'space-between';
            case 'around':
                return 'space-around';
            case 'evenly':
                return 'space-evenly';
            default:
                return v;
        }
    }

    private normalizeAlign(v: UIFlexAlign): string {
        switch (v) {
            case 'start':
                return 'flex-start';
            case 'end':
                return 'flex-end';
            default:
                return v;
        }
    }
}

import { Directive, inject, input, Renderer2, AfterViewInit, ElementRef, OnDestroy } from '@angular/core';
import { FloatingUtility, FloatingProps } from './floating';

export type FloatingDirectiveProps = Omit<FloatingProps, 'floating'>;

/**
 * A directive to position an element relative to a reference element using floating UI logic.
 *
 * @name UIFloatingDirective
 * @phase Utility
 */
@Directive({
    selector: '[ui-floating]',
    host: {
        style: 'position: absolute;',
    },
})
export class UIFloatingDirective implements AfterViewInit, OnDestroy {
    render = inject(Renderer2);
    host = inject(ElementRef);

    floating = new FloatingUtility(this.render);

    readonly props = input.required<FloatingDirectiveProps>({ alias: 'ui-floating' });

    ngAfterViewInit(): void {
        const nextProps: FloatingProps = {
            ...this.props(),
            floating: this.host.nativeElement,
        };

        this.floating.compute(nextProps);
        window.addEventListener('scroll', this.updateFloatingPosition, true);
        window.addEventListener('resize', this.updateFloatingPosition, true);
    }

    ngOnDestroy() {
        window.removeEventListener('scroll', this.updateFloatingPosition, true);
        window.removeEventListener('resize', this.updateFloatingPosition, true);
    }

    updateFloatingPosition = () => {
        const nextProps: FloatingProps = {
            ...this.props(),
            floating: this.host.nativeElement,
        };
        this.floating.compute(nextProps);
    };
}

import { DOCUMENT } from '@angular/common';
import { ElementRef, OnChanges, OnDestroy, input, signal, inject, Directive } from '@angular/core';

/** Safely focus an element if it exists and has a focus method. */
function safeFocus(el: HTMLElement | null | undefined) {
    if (el && 'focus' in el && typeof el.focus === 'function') el.focus();
}

/**
 * Utility that traps keyboard focus within its projected content when enabled.
 *
 * @example
 *     <ui-focus-trap>
 *     <!-- interactive content -->
 *     <button>One</button>
 *     <button>Two</button>
 *     </ui-focus-trap>
 *
 * @name FocusTrap
 * @phase Utility
 */
@Directive({
    selector: '[ui-focus-trap]',
    standalone: true,
})
export class UIFocusTrapDirective implements OnChanges, OnDestroy {
    /** Enables the focus trap. */
    readonly enabled = input<boolean>(true, {
        alias: 'ui-focus-trap',
    });
    /** Auto-focus first focusable child on enable. @default true */
    readonly autoFocus = input<boolean>(true);
    /** Restore focus to previously focused element on disable/destroy. @default true */
    readonly restoreFocus = input<boolean>(true);

    readonly container = inject<ElementRef<HTMLElement>>(ElementRef);

    document = inject(DOCUMENT);
    private readonly bound = signal(false);
    private readonly prevFocused = signal<HTMLElement | null>(null);

    ngOnChanges() {
        const active = this.enabled();
        if (active && !this.bound()) {
            this.attach();
            if (this.autoFocus()) queueMicrotask(() => this.focusInitial());
        } else if (!active && this.bound()) {
            this.detach();
            this.restorePreviousFocus();
        }
    }

    ngOnDestroy() {
        this.detach();
        this.restorePreviousFocus();
    }

    private attach() {
        this.prevFocused.set(this.document?.activeElement as HTMLElement | null);
        this.document?.addEventListener('keydown', this.onKeyDown, true);
        this.document?.addEventListener('focusin', this.onFocusIn, true);
        this.bound.set(true);
    }

    private detach() {
        this.document?.removeEventListener('keydown', this.onKeyDown, true);
        this.document?.removeEventListener('focusin', this.onFocusIn, true);
        this.bound.set(false);
    }

    private restorePreviousFocus() {
        if (!this.restoreFocus()) return;
        safeFocus(this.prevFocused());
        this.prevFocused.set(null);
    }

    private onKeyDown = (e: Event) => {
        if (!(e instanceof KeyboardEvent)) return;
        if (!this.enabled()) return;
        const root = this.container.nativeElement;
        if (!root) return;
        if (e.key === 'Tab') {
            const focusables = this.getFocusableElements();
            if (focusables.length === 0) {
                e.preventDefault();
                root.focus();
                return;
            }
            const active = this.document?.activeElement as HTMLElement | null;
            let index = focusables.findIndex((el) => el === active);
            if (index === -1) index = e.shiftKey ? focusables.length - 1 : 0;
            const nextIndex = e.shiftKey
                ? (index - 1 + focusables.length) % focusables.length
                : (index + 1) % focusables.length;
            e.preventDefault();

            safeFocus(focusables[nextIndex]);
        }
    };

    private onFocusIn = (e: Event) => {
        if (!this.enabled()) return;
        const target = e.target as HTMLElement | null;
        const root = this.container.nativeElement;
        if (!root || !target) return;
        if (!root.contains(target)) {
            const focusables = this.getFocusableElements();
            safeFocus(focusables[0] || root);
        }
    };

    private focusInitial() {
        const root = this.container.nativeElement;
        const focusables = this.getFocusableElements();
        safeFocus(focusables[0] || root);
    }

    private getFocusableElements(): HTMLElement[] {
        const root = this.container.nativeElement;
        if (!root) return [];
        const selector = [
            'a[href]',
            'button:not([disabled])',
            'input:not([disabled])',
            'textarea:not([disabled])',
            'select:not([disabled])',
            '[tabindex]:not([tabindex="-1"])',
        ].join(',');
        const nodes = Array.from(root.querySelectorAll<HTMLElement>(selector));
        return nodes.filter((el) => this.isFocusable(el));
    }

    private isFocusable(el: HTMLElement): boolean {
        const style = window.getComputedStyle(el);
        const rect = el.getBoundingClientRect();
        const notHidden = style.visibility !== 'hidden' && style.display !== 'none';
        const inDoc = !!el.ownerDocument && !!el.ownerDocument.documentElement.contains(el);
        const enabled = !(el as HTMLButtonElement).disabled;
        const inBounds = rect.width > 0 && rect.height > 0;
        return notHidden && inDoc && enabled && inBounds;
    }
}

import { Directive, input, AfterViewInit, inject, ElementRef, OnDestroy } from '@angular/core';
import { KeyNavigationUtility, KeyNavigationUtilityProps } from './key-navigation';

/**
 * A directive to enable key navigation on an element.
 *
 * @name UIKeyNavigationDirective
 * @phase Utility
 */
@Directive({
    selector: '[ui-key-navigation]',
    host: {
        '(keydown)': 'handleKeydown($event)',
    },
})
export class UIKeyNavigationDirective implements AfterViewInit, OnDestroy {
    host = inject<ElementRef<HTMLElement>>(ElementRef<HTMLElement>);

    readonly props = input<KeyNavigationUtilityProps>(
        {
            activeElementId: null,
        },
        { alias: 'ui-key-navigation' },
    );

    private keyNavigation = new KeyNavigationUtility();

    handleKeydown(event: KeyboardEvent) {
        this.keyNavigation.handleKeydown(event);
    }

    ngAfterViewInit(): void {
        this.keyNavigation.init(this.props());
    }

    ngOnDestroy(): void {
        this.keyNavigation.destroy();
    }
}

import {
    Directive,
    ElementRef,
    Renderer2,
    inject,
    input,
    OnDestroy,
    effect,
    EffectRef,
    ViewContainerRef,
    EnvironmentInjector,
} from '@angular/core';
import { AsInputSignal } from '../../types/common';

export interface LinkProps {
    /** The variant of the link. Controls the icon that is displayed and link target. */
    trailingIcon?: 'chevron' | 'external' | 'link';
    /**
     * The href of the link.
     *
     * @example
     *     https://bspk.dev
     */
    href: string | null | undefined;
    /**
     * The size of the link.
     *
     * @default base
     */
    size?: 'base' | 'large' | 'small';
    /**
     * Change the color of the link to a subtle color. This is useful for links that are not primary actions, for
     * example footer menus.
     *
     * @default default
     */
    variant?: 'default' | 'subtle-inverse' | 'subtle';
    /**
     * The target of the link. If the `trailingIcon` is set to `external`, this will default to `_blank`.
     *
     * @default _self
     */
    target?: '_blank' | '_parent' | '_self' | '_top';
    /** Determines if the element is disabled. */
    disabled?: boolean;
}

/**
 * This is the standalone link directive. Apply to an `a` element.
 *
 * @example
 *     <a ui-link href="https://anywhere.re" [label]="'Example label'" trailingIcon="external"></a>
 *
 * @name Link
 * @phase Stable
 */
@Directive({
    selector: 'a[ui-link]',
})
export class UILinkDirective implements AsInputSignal<LinkProps>, OnDestroy {
    readonly trailingIcon = input<LinkProps['trailingIcon']>();
    readonly href = input<LinkProps['href']>();
    readonly size = input<LinkProps['size']>('base');
    readonly variant = input<LinkProps['variant']>('default');
    readonly target = input<LinkProps['target']>('_self');
    readonly disabled = input<LinkProps['disabled']>(false);

    private readonly host = inject<ElementRef<HTMLAnchorElement>>(ElementRef);
    private readonly renderer = inject(Renderer2);
    private env = inject(EnvironmentInjector);

    private labelSpan: HTMLSpanElement | null = null;
    private teardown: EffectRef | null = null;

    constructor(private viewContainerRef: ViewContainerRef) {
        this.teardown = effect(async () => {
            const el = this.host.nativeElement;

            // Set base attributes
            this.renderer.setAttribute(el, 'data-bspk', 'link');
            this.renderer.setAttribute(el, 'data-size', this.size() || 'base');
            this.renderer.setAttribute(el, 'data-trailing-icon', this.trailingIcon() || '');

            // Subtle variants
            const v = this.variant();
            if (v === 'subtle') {
                this.renderer.setAttribute(el, 'data-subtle', 'true');
                this.renderer.removeAttribute(el, 'data-subtle-inverse');
            } else if (v === 'subtle-inverse') {
                this.renderer.setAttribute(el, 'data-subtle-inverse', 'true');
                this.renderer.removeAttribute(el, 'data-subtle');
            } else {
                this.renderer.removeAttribute(el, 'data-subtle');
                this.renderer.removeAttribute(el, 'data-subtle-inverse');
            }

            // href, disabled, target
            const hrefVal = this.href();
            if (hrefVal) this.renderer.setAttribute(el, 'href', hrefVal);
            else this.renderer.removeAttribute(el, 'href');

            const disabledVal = this.disabled();
            if (disabledVal) this.renderer.setAttribute(el, 'disabled', 'true');
            else this.renderer.removeAttribute(el, 'disabled');

            const tIcon = this.trailingIcon();
            const tgt = tIcon === 'external' ? '_blank' : this.target() || '_self';
            this.renderer.setAttribute(el, 'target', tgt);

            // wrap text content in span
            if (!this.labelSpan) {
                const span = this.renderer.createElement('span') as HTMLSpanElement;
                span.textContent = el.textContent;
                this.renderer.setProperty(el, 'textContent', '');
                this.renderer.appendChild(el, span);
                this.labelSpan = span;
            }

            const iconComponent = await this.iconComponent();
            if (iconComponent) {
                const icon = this.viewContainerRef.createComponent(iconComponent);
                this.renderer.appendChild(el, icon.location.nativeElement);
            }
        });
    }

    iconComponent() {
        const tIcon = this.trailingIcon();
        if (tIcon === 'external') return import('../icons/open-in-new').then((m) => m.IconOpenInNew);
        if (tIcon === 'chevron') return import('../icons/chevron-right').then((m) => m.IconChevronRight);
        if (tIcon === 'link') return import('../icons/link').then((m) => m.IconLink);
        return null;
    }

    ngOnDestroy(): void {
        if (this.teardown) this.teardown.destroy();
        this.labelSpan = null;
    }
}

