import { Directive, ElementRef, OnDestroy, AfterViewInit, Renderer2, inject } from '@angular/core';

/**
 * Sets the host element's height to match its parent element's clientHeight. Updates on window resize and orientation
 * changes.
 *
 * @example
 *     <div style="height: 400px">
 *     <div ui-match-parent-height>Content fits parent height</div>
 *     </div>
 *
 * @name MatchParentHeight
 * @phase Utility
 */
@Directive({
    selector: '[ui-match-parent-height]',
    standalone: true,
})
export class UIMatchParentHeightDirective implements AfterViewInit, OnDestroy {
    el = inject<ElementRef<HTMLElement>>(ElementRef);
    renderer = inject(Renderer2);

    private removeResize?: () => void;
    private removeOrientation?: () => void;

    ngAfterViewInit(): void {
        this.updateHeight();
        this.removeResize = this.renderer.listen('window', 'resize', () => this.updateHeight());
        this.removeOrientation = this.renderer.listen('window', 'orientationchange', () => this.updateHeight());
    }

    ngOnDestroy(): void {
        if (this.removeResize) this.removeResize();
        if (this.removeOrientation) this.removeOrientation();
    }

    private updateHeight(): void {
        const element = this.el.nativeElement;
        const parent = element?.parentElement;
        if (!element || !parent) return;

        // Reset height to allow shrinking or growing
        element.style.height = '';

        // Apply new height on next frame
        requestAnimationFrame(() => {
            element.style.height = `${parent.clientHeight}px`;
        });
    }
}

import { Directive, input, AfterViewInit, inject, ElementRef, OnDestroy } from '@angular/core';
import { OutsideClickUtility, OutsideClickUtilityProps } from './outside-click';

/**
 * A directive to detect clicks outside a specified element and execute a callback.
 *
 * @name UIOutsideClickDirective
 * @phase Utility
 */
@Directive({
    selector: '[ui-outside-click]',
})
export class UIOutsideClickDirective implements AfterViewInit, OnDestroy {
    host = inject<ElementRef<HTMLElement>>(ElementRef<HTMLElement>);

    readonly props = input<OutsideClickUtilityProps>(
        {
            disabled: false,
            handleTabs: true,
            callback: () => {
                // no op
            },
        },
        { alias: 'ui-outside-click' },
    );

    private utility = new OutsideClickUtility();

    ngAfterViewInit(): void {
        const nextProps = {
            elements: [this.host!.nativeElement],
            ...this.props(),
        };

        this.utility.init(nextProps);
    }

    ngOnDestroy(): void {
        this.utility.destroy();
    }
}

import { Directive, ElementRef, OnChanges, OnDestroy, SimpleChanges, input } from '@angular/core';

export type PortalContainer = HTMLElement | (() => HTMLElement | null | undefined) | null | undefined;

@Directive({ selector: '[ui-portal]' })
export class UIPortalDirective implements OnDestroy, OnChanges {
    /** The target element to render the portal into. Defaults to `document.body` in the browser. */
    readonly container = input<PortalContainer>(undefined, { alias: 'ui-portal' });

    constructor(private elRef: ElementRef) {}

    ngOnChanges(changes: SimpleChanges) {
        if (changes['container']) {
            if (typeof document === 'undefined' || !this.elRef?.nativeElement) return;

            this.elRef.nativeElement.parentNode.removeChild(this.elRef.nativeElement);
            // Move the element to the new target

            let targetElement: HTMLElement | null | undefined;

            const target = changes['container'].currentValue;

            if (typeof target === 'function') {
                targetElement = target();
            } else {
                targetElement = target || document.body;
            }

            targetElement?.appendChild(this.elRef.nativeElement);
        }
    }

    ngOnDestroy() {
        if (this.elRef.nativeElement.parentNode) {
            this.elRef.nativeElement.parentNode.removeChild(this.elRef.nativeElement);
        }
    }
}

import { DOCUMENT } from '@angular/common';
import {
    Component,
    ComponentRef,
    Directive,
    ElementRef,
    EnvironmentInjector,
    OnDestroy,
    OnInit,
    Renderer2,
    ViewEncapsulation,
    computed,
    effect,
    inject,
    model,
    signal,
    viewChild,
} from '@angular/core';
import { Placement } from '@floating-ui/dom';
import { addComponent } from '../../utils/add-component';
import { uniqueId } from '../../utils/random';
import { FloatingUtility } from '../floating/floating';

export type TooltipPlacement = Extract<Placement, 'bottom' | 'left' | 'right' | 'top'>;

export interface TooltipProps {
    /** The tooltip content. */
    label?: string;
    /**
     * The placement of the tooltip.
     *
     * @default top
     */
    placement?: TooltipPlacement;
    /**
     * Whether to visually show the arrow (tail).
     *
     * @default true
     */
    showTail?: boolean;
    /**
     * Determines if the tooltip is disabled.
     *
     * @default false
     */
    disabled?: boolean;
}

/**
 * Brief message that provide additional guidance and helps users perform an action if needed.
 *
 * @example
 *     <span [ui-tooltip]="{
 *     label: 'I explain what this button does',
 *     placement: 'top',
 *     }">Hover me</span>
 *
 * @name Tooltip
 * @phase Dev
 */
@Directive({
    selector: '[ui-tooltip]',
    standalone: true,
    host: {
        '(mouseenter)': 'handleOpenEvent($event)',
        '(mouseleave)': 'handleCloseEvent()',
        '(focusin)': 'handleOpenEvent($event)',
        '(blur)': 'handleCloseEvent()',
    },
})
export class UITooltipDirective implements OnDestroy, OnInit {
    readonly value = model<TooltipProps | string | { truncated: true } | undefined>(undefined, {
        alias: 'ui-tooltip',
    });

    readonly tooltipId = uniqueId('tooltip');

    readonly props = computed(() => {
        const value = this.value();

        const next: TooltipProps & { truncated?: true } = {};

        if (!value) return next;

        if (typeof value === 'string') {
            return { ...next, label: value.trim() };
        } else {
            return { ...next, ...value };
        }
    });

    host = inject<ElementRef<HTMLElement>>(ElementRef);
    renderer = inject(Renderer2);
    env = inject(EnvironmentInjector);
    document = inject<Document>(DOCUMENT);

    floating = new FloatingUtility(this.renderer);

    private readonly computedPlacement = signal<TooltipPlacement | null>(null);
    private tooltipComponent?: ComponentRef<UITooltip> | null;

    constructor() {
        effect(() => {
            this.updateTooltipProps(this.props());
        });
    }

    get arrowEl() {
        return this.tooltipComponent?.instance.arrowElement;
    }

    get tooltipEl() {
        return this.tooltipComponent?.location.nativeElement as HTMLElement;
    }

    get referenceEl() {
        let el = this.host.nativeElement;
        if (!el?.checkVisibility?.()) el = (el?.firstElementChild as HTMLElement) || null;
        return el;
    }

    updateTooltipProps(props: TooltipProps) {
        if (!this.tooltipComponent) return;
        this.tooltipComponent.instance.props.set({
            ...props,

            // ensure placement is always up to date
            placement: this.computedPlacement() || props.placement,
        });
        this.tooltipComponent.changeDetectorRef.detectChanges();
    }

    handleOpenEvent(event: Event) {
        const props = this.props();

        if (
            !props.label ||
            event.target !== this.referenceEl ||
            props.disabled ||
            // skip if not actively truncated
            (props.truncated && this.referenceEl?.scrollWidth <= this.referenceEl?.clientWidth)
        )
            return;

        if (props.truncated) this.addComponent({ label: this.referenceEl.textContent || '' });

        if (this.tooltipEl) this.renderer.setStyle(this.tooltipEl, 'display', 'block');

        this.floating
            .compute({
                placement: this.props()?.placement || 'top',
                reference: this.referenceEl,
                floating: this.tooltipEl,
                arrow: this.arrowEl,
                offsetOptions: this.arrowEl ? 8 : 4,
                refWidth: false,
            })
            .then(({ placement }) => {
                if (this.tooltipEl && placement !== this.props()?.placement)
                    this.computedPlacement.set(placement as TooltipPlacement);
            });
    }

    handleCloseEvent() {
        const { label, truncated } = this.props();
        if (!label) return;

        if (truncated) this.removeComponent();
        if (this.tooltipEl) this.renderer.setStyle(this.tooltipEl, 'display', 'none');
    }

    ngOnDestroy(): void {
        this.handleCloseEvent();
        if (this.referenceEl) this.renderer.removeAttribute(this.referenceEl, 'aria-labelledby');

        this.removeComponent();
    }

    removeComponent() {
        if (this.tooltipComponent) {
            this.renderer.removeChild(this.tooltipEl.parentNode, this.tooltipEl);
            this.tooltipComponent.destroy();
            this.tooltipComponent = null;
        }
    }

    addComponent(props = this.props()): boolean {
        if (
            // if we can't render or already rendered or disabled or no label we can't show
            !this.document ||
            !this.referenceEl ||
            !props.label
        ) {
            return false;
        }

        this.tooltipComponent = addComponent(this.env, UITooltip, 'ui-tooltip')!;

        if (this.tooltipEl) this.renderer.setStyle(this.tooltipEl, 'display', 'none');
        this.tooltipComponent.instance.id.set(this.tooltipId);
        this.updateTooltipProps(props);
        return true;
    }

    ngOnInit(): void {
        if (!this.referenceEl || !this.props().label) return;

        if (this.props().truncated) {
            this.renderer.setAttribute(this.referenceEl, 'data-truncated', 'true');
        } else {
            this.renderer.setAttribute(this.referenceEl, 'aria-labelledby', this.tooltipId);
            this.addComponent(this.props());
        }
    }
}

/** Single use component to display tooltip content. */
@Component({
    selector: 'ui-tooltip',
    standalone: true,
    template: `
        <span data-text>{{ props().label }}</span>
        @if (props().showTail !== false) {
            <span aria-hidden="true" data-arrow #arrow></span>
        }
    `,
    styleUrl: './tooltip.scss',
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'tooltip',
        role: 'tooltip',
        '[attr.data-placement]': 'props().placement || "top"',
        '[attr.id]': 'id() || null',
    },
})
export class UITooltip {
    readonly arrow = viewChild<ElementRef>('arrow');

    /** Tooltip id for a11y labelling */
    readonly id = signal<string | undefined>(undefined);

    readonly props = model<TooltipProps>({});

    get arrowElement(): HTMLElement | null {
        return this.arrow()?.nativeElement || null;
    }
}

