    border-radius: var(--radius-full);
    background-color: var(--background);
    color: var(--foreground);
    font: var(--font);
    flex-shrink: 0;
    flex-grow: 0;
    position: relative;

    &:has(img) {
        overflow: hidden;
        align-items: start;
        justify-content: start;
    }

    img {
        vertical-align: top;
