import { CommonModule } from '@angular/common';
import { Component, input, signal, ViewEncapsulation } from '@angular/core';
import { uniqueId } from '../../utils/random';
import { UIAvatar, UIAvatarProps, SizeVariant } from '../avatar/avatar';
import { UIAvatarGroupOverflow } from './overflow';

export type AvatarItem = Pick<UIAvatarProps, 'color' | 'image' | 'initials' | 'name' | 'showIcon' | 'size'>;

/**
 * The AvatarGroup component is used to display a group of avatars, with support for overflow handling and different
 * display variants.
 *
 * @example
 *     <AvatarGroup
 *     items={[
 *     { name: 'Fezzik', image: '/avatar-01.png' },
 *     { name: 'Inigo Montoya', initials: 'IM', color: 'blue' },
 *     { name: 'Miracle Max', initials: 'MM', color: 'green' },
 *     { name: 'Princess Buttercup', showIcon: true },
 *     { name: 'Westley', initials: 'W', color: 'purple' },
 *     { name: 'Vincent', initials: 'V', image: '/avatar-04.png' },
 *     ]}
 *     size="medium"
 *     max={3}
 *     variant="spread"
 *     />;
 *
 * @name AvatarGroup
 * @phase Dev
 */

@Component({
    selector: 'ui-avatar-group',
    standalone: true,
    imports: [CommonModule, UIAvatar, UIAvatarGroupOverflow],
    template: `
        <div
            data-bspk="avatar-group"
            [attr.data-max]="max()"
            [attr.data-size]="size()"
            [attr.data-variant]="variant()"
            [ngStyle]="style">
            <div data-wrap>
                @for (item of visibleItems; track item.id) {
                    <ui-avatar
                        [name]="item.name"
                        [image]="item.image"
                        [initials]="item.initials"
                        [color]="item.color"
                        [showIcon]="item.showIcon"
                        [id]="item.id"
                        [size]="size()"
                        (click)="onAvatarClick()"></ui-avatar>
                }
                @if (overflowItems.length > 0) {
                    <button
                        #overflowBtn
                        [attr.aria-activedescendant]="activeElementId() || null"
                        [attr.aria-controls]="open() ? menuId : null"
                        [attr.aria-expanded]="open()"
                        aria-haspopup="menu"
                        [attr.aria-label]="overflowButtonLabel"
                        data-bspk="avatar"
                        data-bspk-owner="avatar-overflow"
                        [attr.data-size]="size()"
                        (blur)="closeMenu()"
                        (click)="toggleMenu()"
                        (keydown)="handleKeydown($event)"
                        role="combobox">
                        <span data-overflow-count>+{{ overflowItems.length }}</span>
                    </button>
                    <ui-avatar-group-overflow
                        [open]="open()"
                        [activeElementId]="activeElementId()"
                        [menuId]="menuId()!"
                        [items]="overflowItems"
                        [size]="size()"
                        [menuReference]="overflowBtn" />
                }
            </div>
        </div>
    `,
    styleUrl: './avatar-group.scss',
    encapsulation: ViewEncapsulation.None,
    host: {
        style: `display: contents;`,
    },
})
export class UIAvatarGroup {
    readonly items = input<any[]>([]);
    readonly size = input<SizeVariant>('small');
    readonly max = input<number>(5);
    readonly variant = input<'spread' | 'stacked'>('stacked');
    readonly style = input<Record<string, string> | null>(null);
    readonly open = signal<boolean>(false);
    readonly activeElementId = signal<string | null>(null);
    readonly menuId = input<string>(uniqueId('avatar-group-menu'));

    get visibleItems(): (AvatarItem & { id: string })[] {
        // Use .value() for signals created by input()
        const items = this.items();
        const max = this.max();
        return items.slice(0, max).map((item, idx) => ({
            ...item,
            id: `${item.name?.replace(/\s+/g, '-').toLowerCase()}-${idx}`,
        }));
    }

    get overflowItems(): (AvatarItem & { id: string })[] {
        const items = this.items();
        const max = this.max();
        return items.slice(max).map((item, idx) => ({
            ...item,
            id: `${item.name?.replace(/\s+/g, '-').toLowerCase()}-overflow-${idx}`,
        }));
    }

    get overflowButtonLabel(): string {
        const count = this.overflowItems.length;
        return `Show ${count} more avatar${count > 1 ? 's' : ''}`;
    }

    handleKeydown(event: KeyboardEvent) {
        if (!this.open) return;
        const items = this.items();
        const idx = items.findIndex((i) => i.id === this.activeElementId());
        if (event.key === 'ArrowDown') {
            const nextIdx = (idx + 1) % items.length;
            this.activeElementId.set(items[nextIdx].id);
            event.preventDefault();
        } else if (event.key === 'ArrowUp') {
            const prevIdx = (idx - 1 + items.length) % items.length;
            this.activeElementId.set(items[prevIdx].id);
            event.preventDefault();
        } else if (event.key === 'Escape') {
            this.open.set(false);
            event.preventDefault();
        }
    }

    toggleMenu() {
        this.open.set(!this.open());
        const items = this.items();
        if (this.open() && items.length) {
            this.activeElementId.set(items[0].id);
        } else {
            this.activeElementId.set(null);
        }
    }

    closeMenu() {
        this.open.set(false);
    }

    onAvatarClick() {
        // Placeholder for avatar click handling
    }
}

import { CommonModule } from '@angular/common';
import { Component, input, viewChild, ElementRef, ViewEncapsulation, model } from '@angular/core';

// Assume these are your Angular equivalents:
import { UIAvatar, UIAvatarProps } from '../avatar/avatar';
import { UIFloatingDirective } from '../floating';
import { UIListItem } from '../list-item/list-item';
import { UIMenu } from '../menu/menu';

@Component({
    selector: 'ui-avatar-group-overflow',
    standalone: true,
    imports: [CommonModule, UIAvatar, UIListItem, UIMenu, UIFloatingDirective],
    template: `
        @if (open()) {
            <ng-container>
                <ui-menu
                    [ui-floating]="{ reference: menuReference(), offsetOptions: offset }"
                    [id]="menuId()"
                    [style.width]="'fit-content'"
                    [style.paddingRight]="'var(--spacing-sizing-04)'"
                    [style.--list-item-height]="'var(--spacing-sizing-12)'"
                    [style.maxHeight]="maxMenuHeight"
                    [ariaRole]="'menu'">
                    @for (item of items(); track item.id) {
                        <ui-list-item
                            [ariaRole]="'menuitem'"
                            [active]="activeElementId() === item.id"
                            [label]="item.name">
                            <span data-leading>
                                <ui-avatar
                                    [hideTooltip]="true"
                                    [size]="'small'"
                                    [id]="item.id"
                                    [name]="item.name"
                                    [image]="item.image"
                                    [color]="item.color"
                                    [showIcon]="item.showIcon"
                                    [initials]="item.initials">
                                </ui-avatar>
                            </span>
                        </ui-list-item>
                    }
                </ui-menu>
            </ng-container>
        }
        <ng-template #avatar let-item>
            <ui-avatar
                [hideTooltip]="true"
                [size]="'small'"
                [id]="item.id"
                [name]="item.name"
                [image]="item.image"
                [color]="item.color"
                [showIcon]="item.showIcon"
                [initials]="item.initials"></ui-avatar>
        </ng-template>
    `,
    encapsulation: ViewEncapsulation.None,
    host: {
        style: `display: contents;`,
    },
})
export class UIAvatarGroupOverflow {
    readonly overflowBtn = viewChild<ElementRef<HTMLButtonElement>>('overflowBtn');
    readonly size = input<string>('medium');
    readonly items = input.required<UIAvatarProps[]>();
    readonly open = model<boolean>(false);
    readonly menuId = input.required<string>();
    readonly activeElementId = model<string | null>(null);
    readonly menuReference = input.required<HTMLElement>();

    get offset() {
        // Reads the CSS variable value at runtime, offsetOptions requires a number
        return parseInt(getComputedStyle(document.documentElement).getPropertyValue('--spacing-sizing-01'));
    }

    get maxMenuHeight() {
        return this.items.length > 5 ? 'calc(var(--spacing-sizing-12) * 5)' : '';
    }

    closeMenu() {
        this.open.set(false);
    }

    toggleMenu() {
        this.open.set(!this.open());
        const items = this.items();
        if (this.open() && items.length) {
            this.activeElementId.set(items[0].id);
        } else {
            this.activeElementId.set(null);
        }
    }

    avatarTemplate(item: UIAvatarProps) {
        return {
            ...item,
            hideTooltip: true,
            size: 'small',
        };
    }
}

import { Component, ElementRef, ViewEncapsulation, input, viewChild } from '@angular/core';

export interface BadgeProps {
    count?: number;
    size?: string;
    color?: string;
    surfaceBorder?: boolean;
}

/**
 * Visual indicator for new items within a parent page represented with a numerical count of new items.
 *
 * @example
 *     import { Badge } from '@bspk/ui/Badge';
 *
 *     <Badge count={5} size="small" color="primary" surfaceBorder={true} />;
 *
 * @exampleDescription This example shows a badge with a count of 5, size small, and primary variant.
 *
 * @name Badge
 * @phase Dev
 */

@Component({
    selector: 'ui-badge',
    standalone: true,
    template: `<span #contentWrapper><ng-content></ng-content></span>
        <sup
            data-bspk="badge"
            [attr.data-color]="color()"
            [attr.data-size]="size()"
            [attr.data-surface-border]="surfaceBorder() ? true : undefined"
            [attr.data-attachment]="hasProjectedContent ? true : undefined">
            {{ typeof count() === 'number' && count()! > 99 ? '99+' : count() }}
        </sup>`,
    styleUrls: ['./badge.scss'],
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'badge-wrapper',
        'data-attachment-wrapper': '',
    },
})
export class UIBadge {
    /**
     * The context for which the badge is applied.
     *
     * Could be a button, link, or any other element that the badge is associated with.
     */
    readonly contentWrapper = viewChild.required<ElementRef<HTMLElement>>('contentWrapper');

    /**
     * The content of the badge. If larger than 99, the badge will display '99+'.
     *
     * @example
     *     5;
     *
     * @default 1
     */
    readonly count = input<number | undefined>(1);

    /**
     * The size of the badge.
     *
     * @default small
     */
    readonly size = input<'small' | 'x-small'>('small');

    /**
     * The color variant of the badge.
     *
     * @default primary
     */
    readonly color = input<'primary' | 'secondary'>('primary');

    /**
     * Whether the badge should have a border that matches the surface color.
     *
     * @default false
     */
    readonly surfaceBorder = input(false);

    /**
     * Indicates whether the badge has projected (custom) content.
     *
     * This is set to `true` if any content is projected into the badge component (for example,
     * <ui-badge>New</ui-badge>), and `false` if only the `count` input is used. Used internally to determine whether to
     * display the projected content or the count.
     *
     * @internal
     */
    get hasProjectedContent() {
        return !!this.contentWrapper().nativeElement?.childNodes.length;
    }
}

