import { CommonModule } from '@angular/common';
import {
    Component,
    ChangeDetectionStrategy,
    ViewEncapsulation,
    input,
    computed,
    signal,
    viewChild,
    AfterViewInit,
    OnDestroy,
} from '@angular/core';
import { AsInputSignal } from '../../types/common';
import { keydownHandler } from '../../utils/keydown-handler';
import { uniqueId } from '../../utils/random';
import { scrollLimitStyle, ScrollLimitStyleProps } from '../../utils/scroll-limit-style';
import { UIButton } from '../button';
import { UIFloatingDirective } from '../floating';
import { IconChevronRight } from '../icons/chevron-right';
import { IconMoreHoriz } from '../icons/more-horiz';
import { KeyNavigationUtility } from '../key-navigation';
import { UIListItem } from '../list-item';
import { UIMenu } from '../menu';
import { UIOutsideClickDirective } from '../outside-click';
import { BreadcrumbItem } from './utils';

export type BreadcrumbDropdownProps = ScrollLimitStyleProps & {
    items: BreadcrumbItem[];
    id?: string;
};

/**
 * The BreadcrumbDropdown component is used to display a dropdown menu within a breadcrumb navigation.
 *
 * @name BreadcrumbDropdown
 * @parent Breadcrumb
 */
@Component({
    selector: 'ui-breadcrumb-dropdown',
    imports: [
        CommonModule,
        UIButton,
        UIListItem,
        UIMenu,
        IconChevronRight,
        UIFloatingDirective,
        UIOutsideClickDirective,
    ],
    template: `
        <ui-button
            [icon]="iconMoreHoriz"
            [label]="'Access to ' + items().length + ' pages'"
            [iconOnly]="true"
            variant="tertiary"
            size="small"
            [ariaExpanded]="!!open() || null"
            [ariaHaspopup]="'listbox'"
            [ariaControls]="open() ? menuId() : null"
            (onClick)="toggleMenu()"
            #reference
            (keydown)="handleKeydown($event)"
            [id]="safeId() + '-reference'" />
        @if (open()) {
            <ui-menu
                [ui-outside-click]="{ callback: handleOutsideClick.bind(this) }"
                [ui-floating]="{ reference: referenceEl, offsetOptions: offset }"
                [id]="menuId()"
                label="Expanded breadcrumb"
                owner="Breadcrumb"
                [ariaRole]="'menu'"
                [ngStyle]="ngMenuStyle()">
                @for (item of menuItems(); track item.href) {
                    <ui-list-item
                        as="a"
                        [ariaRole]="'menuitem'"
                        [tabIndex]="-1"
                        [id]="item.id"
                        [label]="item.label"
                        [href]="item.href"
                        [active]="keyNavigation.activeElementId === item.id" />
                }
            </ui-menu>
        }
        <icon-chevron-right aria-hidden="true" width="24" />
    `,
    changeDetection: ChangeDetectionStrategy.OnPush,
    encapsulation: ViewEncapsulation.None,
    host: {
        style: 'display: contents;',
    },
})
export class UIBreadcrumbDropdown implements AsInputSignal<BreadcrumbDropdownProps>, AfterViewInit, OnDestroy {
    readonly iconChevronRight = IconChevronRight;
    readonly iconMoreHoriz = IconMoreHoriz;

    keyNavigation = new KeyNavigationUtility();

    readonly reference = viewChild<UIButton>('reference');

    readonly id = input<BreadcrumbDropdownProps['id']>(uniqueId('dropdown'));
    readonly safeId = computed(() => this.id() || uniqueId('dropdown'));
    readonly menuId = computed(() => `${this.safeId()}-menu`);
    readonly scrollLimit = input<BreadcrumbDropdownProps['scrollLimit']>();
    readonly open = signal<boolean>(false);
    readonly items = input.required<BreadcrumbDropdownProps['items']>();
    readonly menuItems = computed(() =>
        this.items().map((item, index) => ({
            ...item,
            id: `${this.safeId()}-item-${index}`,
        })),
    );
    readonly ids = computed(() => this.menuItems().map((item) => item.id));

    readonly ngMenuStyle = computed(() => {
        return {
            ...scrollLimitStyle(this.scrollLimit(), this.items().length),
            width: 'fit-content',
            maxWidth: '300px',
            minWidth: '150px',
        };
    });

    get offset() {
        // Reads the CSS variable value at runtime, offsetOptions requires a number
        return parseInt(getComputedStyle(document.documentElement).getPropertyValue('--spacing-sizing-01'));
    }

    get referenceEl() {
        return this.reference()!.nativeElement as HTMLElement;
    }

    handleOutsideClick = (): void => {
        this.open.set(false);
    };

    handleKeydown(event: KeyboardEvent): void {
        if (!this.open()) {
            keydownHandler({
                ArrowDown: () => this.toggleMenu(true),
                'Ctrl+Option+Space': () => this.toggleMenu(true),
                Space: () => this.toggleMenu(true),
                Enter: () => this.toggleMenu(true),
            })(event);
            return;
        }

        const SpaceEnter = () => this.keyNavigation.activeElement?.click();

        // all key events run through keyNavigation utility when menu is open
        this.keyNavigation.handleKeydown(event, {
            'Ctrl+Option+Space': SpaceEnter,
            Space: SpaceEnter,
            Enter: SpaceEnter,
            Escape: () => {
                if (this.open()) this.toggleMenu(false);
            },
        });
    }

    ngAfterViewInit(): void {
        this.keyNavigation.init({ ids: this.ids() });
    }

    ngOnDestroy(): void {
        this.keyNavigation.destroy();
    }

    toggleMenu(force?: boolean) {
        if (force !== undefined) {
            this.open.set(force);
            return;
        }
        this.open.update((o) => !o);
    }
}

import { CommonModule } from '@angular/common';
import {
    Component,
    Output,
    EventEmitter,
    ElementRef,
    ChangeDetectionStrategy,
    ViewEncapsulation,
    input,
    viewChild,
} from '@angular/core';

import { BspkIcon } from '../../types/bspk-icon';

import { ButtonSize } from '../../types/common';
import { UIIcon } from '../icon';
import { UITooltipDirective } from '../tooltip';

export type ButtonVariant = 'primary' | 'secondary' | 'tertiary';
export type ButtonWidth = 'fill' | 'hug';
export type IconType = BspkIcon;

/**
 * A clickable component that allows users to perform an action, make a choice or trigger a change in state.
 *
 * @example
 *     ```html
 *     <bspk-button
 *       label="Click Me"
 *       size="medium"
 *       variant="primary"
 *       (onClick)="handleClick($event)"
 *       icon="iconTemplate">
 *     </bspk-button>
 *     ```;
 *
 * @name Button
 * @phase Dev
 */
@Component({
    selector: 'ui-button',
    template: `<button
        [ui-tooltip]="tooltipLabel"
        #buttonElement
        [type]="type()"
        [attr.aria-label]="ariaLabel() || label()"
        [attr.aria-controls]="ariaControls() || null"
        [attr.aria-expanded]="ariaExpanded() || null"
        [attr.aria-haspopup]="ariaHaspopup() || null"
        [attr.data-bspk]="'button'"
        [attr.data-bspk-owner]="owner() || null"
        [attr.data-destructive]="destructive() || null"
        [attr.data-size]="size()"
        [attr.data-touch-target-parent]="true"
        [attr.data-variant]="variant()"
        [attr.data-width]="width()"
        [disabled]="disabled() || null"
        [class]="buttonClasses"
        (click)="handleClick($event)"
        (focus)="handleFocus($event)"
        (blur)="handleBlur($event)"
        (mouseover)="handleMouseOver($event)"
        (mouseleave)="handleMouseLeave($event)"
        [style]="style()">
        @if (!label()) {
            <ng-content></ng-content>
        } @else {
            @if (icon()) {
                <span [attr.aria-hidden]="true" [attr.data-button-icon]="true">
                    <ui-icon [icon]="icon()!"></ui-icon>
                </span>
            }
            @if (shouldShowLabel) {
                <span [attr.data-button-label]="true">{{ label() }}</span>
            }
        }
        <span [attr.data-touch-target]="true"></span>
    </button>`,
    styleUrls: ['./button.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
    imports: [CommonModule, UIIcon, UITooltipDirective],
    encapsulation: ViewEncapsulation.None,
    host: {
        style: 'display: contents;',
    },
})
export class UIButton {
    /** Event emitted when the button is clicked. */
    @Output() onClick = new EventEmitter<MouseEvent>();

    /** Event emitted when the button receives focus. */
    @Output() onFocus = new EventEmitter<FocusEvent>();

    /** Event emitted when the button loses focus. */
    @Output() onBlur = new EventEmitter<FocusEvent>();

    /** Event emitted when mouse enters the button. */
    @Output() onMouseOver = new EventEmitter<MouseEvent>();

    /** Event emitted when mouse leaves the button. */
    @Output() onMouseLeave = new EventEmitter<MouseEvent>();

    readonly buttonElement = viewChild.required<ElementRef<HTMLButtonElement>>('buttonElement');

    readonly style = input<string>();

    /**
     * The label of the button.
     *
     * @required
     */
    readonly label = input.required<string>();

    /** Use only for custom buttons. The aria-label of the button for accessibility purposes. */
    readonly ariaLabel = input<string>();

    /** The aria-haspopup attribute of the button for accessibility purposes. */
    readonly ariaHaspopup = input<string>();

    /** The aria-expanded attribute of the button for accessibility purposes. */
    readonly ariaExpanded = input<boolean | null>(null);

    /** The aria-controls attribute of the button for accessibility purposes. */
    readonly ariaControls = input<string | null>(null);

    /**
     * The icon of the button.
     *
     * Should be a SVG from the BSPK icon library.
     */
    readonly icon = input<IconType | undefined>();

    /**
     * When true the button label is hidden and only the icon is shown. When label isn't showing it is used in a tooltip
     * and as the aria-label prop.
     *
     * Ignored if `icon` is not provided.
     *
     * @default false
     */
    readonly iconOnly = input(false);

    /**
     * The function of the button is destructive.
     *
     * @default false
     */
    readonly destructive = input(false);

    /**
     * The size of the button.
     *
     * @default medium
     */
    readonly size = input<ButtonSize>('medium');

    /**
     * The color variant of the button.
     *
     * @default primary
     */
    readonly variant = input<ButtonVariant>('primary');

    /**
     * The width of the button.
     *
     * @default hug
     */
    readonly width = input<ButtonWidth>('hug');

    /** The tool tip text that appears when hovered. */
    readonly tooltip = input<string>();

    /**
     * Whether the button is disabled.
     *
     * @default false
     */
    readonly disabled = input(false);

    /**
     * The button type attribute.
     *
     * @default button
     */
    readonly type = input<'button' | 'reset' | 'submit'>('button');

    /** Owner identifier for tracking/analytics. */
    readonly owner = input<string>();

    /** Additional CSS classes to apply. */
    readonly class = input<string>();
    /** Custom content template to override default button content. Not recommended - use for special cases only. */

    get nativeElement(): HTMLButtonElement {
        return this.buttonElement().nativeElement!;
    }

    get shouldShowLabel(): boolean {
        return !this.iconOnly();
    }

    get tooltipLabel(): string | undefined {
        return this.tooltip() || (this.iconOnly() ? this.label() : undefined);
    }

    get buttonClasses(): string {
        const classes: string[] = [];

        if (this.class()) {
            classes.push(this.class()!);
        }

        return classes.join(' ');
    }

    handleClick(event: MouseEvent): void {
        if (!this.disabled()) {
            this.onClick.emit(event);
        }
    }

    handleFocus(event: FocusEvent): void {
        if (!this.disabled()) {
            this.onFocus.emit(event);
        }
    }

    handleBlur(event: FocusEvent): void {
        if (!this.disabled()) {
            this.onBlur.emit(event);
        }
    }

    handleMouseOver(event: MouseEvent): void {
        if (!this.disabled()) {
            this.onMouseOver.emit(event);
        }
    }

    handleMouseLeave(event: MouseEvent): void {
        if (!this.disabled()) {
            this.onMouseLeave.emit(event);
        }
    }
}

