import {
    Component,
    input,
    Output,
    EventEmitter,
    ViewEncapsulation,
    ElementRef,
    viewChild,
    AfterViewInit,
    OnInit,
} from '@angular/core';
import {
    addMonths,
    addYears,
    eachDayOfInterval,
    endOfMonth,
    endOfWeek,
    format,
    isSameDay,
    isValid,
    startOfMonth,
    startOfToday,
    startOfWeek,
} from 'date-fns';
import { CommonProps } from '../../types/common';
import { UIButton } from '../button';
import { IconChevronLeft } from '../icons/chevron-left';
import { IconChevronRight } from '../icons/chevron-right';
import { IconKeyboardDoubleArrowLeft } from '../icons/keyboard-double-arrow-left';
import { IconKeyboardDoubleArrowRight } from '../icons/keyboard-double-arrow-right';
import { getCalendarKeydownHandler, optionIdGenerator, COLUMNS_COUNT } from './utils';

export type CalendarProps = CommonProps<'id'> & {
    /** The currently selected date. */
    value?: Date;
    /**
     * When true, keyboard focus is trapped within the calendar component on initial render.
     *
     * Only applicable if the Calendar is used in a popover like in DatePicker.
     *
     * @default false
     */
    focusTrap?: boolean;
    /** Fires when the date changes with the new date */
    onChange?: (date: Date) => void;
};

/**
 * Allows customers to select the date, month, and year.
 *
 * @example
 *     <ui-calendar (onChange)="handleChange($event)" />
 *
 * @name Calendar
 * @phase Dev
 */

@Component({
    selector: 'ui-calendar',
    standalone: true,
    template: `
        <div data-bspk="calendar" [id]="id()">
            <div data-header>
                <ui-button
                    label="Previous Year"
                    variant="tertiary"
                    size="large"
                    (click)="setActiveDate(addYears(activeDate, -1))"
                    (keydown)="onFirstButtonKeydown($event)"
                    #firstButton
                    [icon]="IconKeyboardDoubleArrowLeft"
                    [iconOnly]="true">
                </ui-button>
                <ui-button
                    label="Previous Month"
                    variant="tertiary"
                    size="large"
                    (click)="setActiveDate(addMonths(activeDate, -1))"
                    [iconOnly]="true"
                    [icon]="IconChevronLeft">
                </ui-button>
                <span data-title>{{ format(activeDate, 'MMMM yyyy') }}</span>
                <ui-button
                    label="Next Month"
                    variant="tertiary"
                    size="large"
                    (click)="setActiveDate(addMonths(activeDate, 1))"
                    [iconOnly]="true"
                    [icon]="IconChevronRight">
                </ui-button>
                <ui-button
                    label="Next Year"
                    variant="tertiary"
                    size="large"
                    (click)="setActiveDate(addYears(activeDate, 1))"
                    #lastButton
                    [iconOnly]="true"
                    [icon]="IconKeyboardDoubleArrowRight">
                </ui-button>
            </div>

            <table role="grid">
                <thead>
                    <tr>
                        <th abbr="Sunday" scope="col">Sun</th>
                        <th abbr="Monday" scope="col">Mon</th>
                        <th abbr="Tuesday" scope="col">Tue</th>
                        <th abbr="Wednesday" scope="col">Wed</th>
                        <th abbr="Thursday" scope="col">Thu</th>
                        <th abbr="Friday" scope="col">Fri</th>
                        <th abbr="Saturday" scope="col">Sat</th>
                    </tr>
                </thead>
                <tbody [id]="gridId" (keydown)="onGridKeydown($event)" #grid tabindex="0">
                    @for (week of rows; track week[0]?.getTime() ?? -1) {
                        <tr>
                            @for (date of week; track date?.getTime() ?? -1) {
                                <td
                                    [attr.aria-label]="format(date, 'do MMMM yyyy')"
                                    [attr.data-selected]="isSameDay(date, activeDate) ? true : null"
                                    [id]="generateOptionId(date)"
                                    (click)="onChange.emit(date)"
                                    [attr.role]="isSameDay(date, activeDate) ? 'gridcell' : null"
                                    [attr.tabindex]="isSameDay(date, activeDate) ? 0 : -1">
                                    {{ format(date, 'd') }}
                                </td>
                            }
                        </tr>
                    }
                </tbody>
            </table>
        </div>
    `,
    imports: [UIButton],
    styleUrl: './calendar.scss',
    encapsulation: ViewEncapsulation.None,
    host: {
        style: 'display: contents;',
    },
})
export class UICalendar implements AfterViewInit, OnInit {
    @Output() onChange = new EventEmitter<Date>();

    IconKeyboardDoubleArrowLeft = IconKeyboardDoubleArrowLeft;
    IconKeyboardDoubleArrowRight = IconKeyboardDoubleArrowRight;
    IconChevronLeft = IconChevronLeft;
    IconChevronRight = IconChevronRight;

    readonly value = input<Date | undefined>(undefined);
    readonly focusTrap = input(false);
    readonly id = input<string | undefined>(undefined);

    activeDate: Date = startOfToday();
    focusDay = false;

    format = format;
    isSameDay = isSameDay;
    addYears = addYears;
    addMonths = addMonths;

    private readonly firstButton = viewChild<ElementRef<HTMLButtonElement>>('firstButton');
    private readonly lastButton = viewChild<ElementRef<HTMLButtonElement>>('lastButton');
    private readonly grid = viewChild<ElementRef<HTMLElement>>('grid');

    get rows(): Date[][] {
        const start = startOfWeek(startOfMonth(this.activeDate), { weekStartsOn: 0 });
        const end = endOfWeek(endOfMonth(this.activeDate), { weekStartsOn: 0 });
        const days = eachDayOfInterval({ start, end });
        const weeks: Date[][] = [];
        for (let i = 0; i < days.length; i += COLUMNS_COUNT) {
            weeks.push(days.slice(i, i + COLUMNS_COUNT));
        }
        return weeks;
    }

    get gridId() {
        return `${this.id() || 'calendar'}-grid`;
    }

    ngOnInit() {
        this.activeDate = this.value() && isValid(this.value()) ? (this.value() as Date) : startOfToday();
    }

    ngAfterViewInit() {
        if (this.focusDay) {
            setTimeout(() => {
                const selectedDateId = this.generateOptionId(this.activeDate);
                const selectedDateElement = this.grid()?.nativeElement?.querySelector<HTMLElement>(
                    `#${selectedDateId}`,
                );
                selectedDateElement?.focus();
                this.focusDay = false;
            }, 0);
        }
    }

    setActiveDate(date: Date) {
        this.activeDate = date;
    }

    generateOptionId(date: Date): string {
        return optionIdGenerator(this.id() || 'calendar')(date);
    }

    onFirstButtonKeydown(event: KeyboardEvent) {
        if (event.shiftKey && event.key === 'Tab') {
            event.preventDefault();
            event.stopPropagation();
            this.focusDay = true;
            setTimeout(() => this.grid?.()?.nativeElement?.focus(), 0);
        }
    }

    onGridKeydown(event: KeyboardEvent) {
        // Use the shared calendar keydown handler from utils
        getCalendarKeydownHandler(
            this.activeDate,
            (date: Date) => {
                this.setActiveDate(date);
            },
            this.rows,
            () => {
                this.focusDay = true;
                setTimeout(() => {
                    const selectedDateId = this.generateOptionId(this.activeDate);
                    const selectedDateElement = this.grid()?.nativeElement?.querySelector<HTMLElement>(
                        `#${selectedDateId}`,
                    );
                    selectedDateElement?.focus();
                    this.focusDay = false;
                }, 0);
            },
        )(event);

        // Tab navigation for focus trap
        if (event.key === 'Tab' && !event.shiftKey) {
            event.preventDefault();
            event.stopPropagation();
            this.firstButton()?.nativeElement?.focus();
        }
    }
}

import { CommonModule } from '@angular/common';
import { Component, signal } from '@angular/core';
import { sendSnackbar } from '../../utils/send-snackbar';
import { UICalendar } from './calendar';

@Component({
    selector: 'ui-calendar-example',
    standalone: true,
    imports: [CommonModule, UICalendar],
    template: `
        <h2>Calendar</h2>

        <h3>Default</h3>
        <ui-calendar (onChange)="handleChange($event)" />

        <h3>With Initial Value</h3>
        <ui-calendar [value]="initialDate()" (onChange)="handleChange($event, 'With Initial Value')" />

        <h3>With Focus Trap</h3>
        <ui-calendar [focusTrap]="true" (onChange)="handleChange($event, 'With Focus Trap')" />

        <h3>With Custom ID</h3>
        <ui-calendar id="custom-calendar" (onChange)="handleChange($event, 'With Custom ID')" />
    `,
})
export class UICalendarExample {
    // Signal for initial value example
    readonly initialDate = signal(new Date(2026, 0, 15)); // January 15, 2026

    handleChange(date: Date, label = 'Default') {
        sendSnackbar(`onChange (${label}): ${date.toDateString()}`);
    }
}

import { Component, ViewEncapsulation, input } from '@angular/core';

export type CardVariant = 'elevated' | 'outlined';

/**
 * Cards are often rectangular and contain various content, such as text, images, icons, multimedia, and interactive
 * elements.
 *
 * They are similar in size and shape to playing cards and are intended to encourage users to click or tap to view more
 * details.
 *
 * @example
 *     import { Card } from '@bspk/ui/card';
 *
 *     <Card variant="elevated" style={{ padding: 'var(--spacing-sizing-04)', maxWidth: '100%', width: '400px' }}>
 *     <h3>Card Title</h3>
 *     <p>This is some content inside the card.</p>
 *     </Card>;
 *
 * @name Card
 * @phase Dev
 */
@Component({
    selector: 'ui-card',
    standalone: true,
    imports: [],
    template: `<ng-content></ng-content>`,
    styleUrl: './card.scss',
    host: {
        'data-bspk': 'card',
        '[attr.data-variant]': 'variant()',
    },
    encapsulation: ViewEncapsulation.None,
})
export class UICard {
    /**
     * Determines how the card border will appear.
     *
     * @default elevated
     */
    readonly variant = input<CardVariant>('elevated');
}

import {
    Component,
    ViewEncapsulation,
    input,
    booleanAttribute,
    ElementRef,
    output,
    viewChild,
    effect,
} from '@angular/core';
import { uniqueId } from '../../utils/random';

/**
 * A control that allows users to choose one or more items from a list or turn a feature on or off. This is the base
 * element and if used directly you must wrap it with a label. More often used in CheckboxOption or CheckboxGroup.
 *
 * @example
 *     <label>
 *     <ui-checkbox
 *     [checked]="checked"
 *     [indeterminate]="indeterminate"
 *     [id]="'sample-checkbox'"
 *     [name]="'sample-checkbox'"
 *     (checkedChange)="checked = $event"
 *     [value]="'sample'"
 *     aria-label="Sample"
 *     />
 *     Checkbox Label
 *     </label>
 *
 * @element
 * @name Checkbox
 * @phase Dev
 */
@Component({
    selector: 'ui-checkbox',
    standalone: true,
    template: `
        <input
            [attr.aria-describedby]="ariaDescribedBy() || undefined"
            [attr.aria-errormessage]="ariaErrorMessage() || undefined"
            [attr.aria-invalid]="invalid() || undefined"
            [attr.aria-label]="ariaLabel()"
            [checked]="checked()"
            [attr.data-indeterminate]="indeterminate() || undefined"
            [disabled]="disabled()"
            [id]="id()"
            [name]="name()"
            [required]="required()"
            [value]="value()"
            type="checkbox"
            (change)="onInputChange()"
            #input />
        <span aria-hidden="true">
            @if (indeterminate()) {
                <svg data-indeterminate fill="none" viewBox="0 0 12 4" xmlns="http://www.w3.org/2000/svg">
                    <path
                        d="M1.44444 3.11089C0.892158 3.11089 0.444443 2.66318 0.444443 2.11089V1.88867C0.444443 1.33639 0.892158 0.888672 1.44444 0.888672H10.5556C11.1078 0.888672 11.5556 1.33639 11.5556 1.88867V2.11089C11.5556 2.66318 11.1078 3.11089 10.5556 3.11089H1.44444Z"
                        fill="currentColor" />
                </svg>
            } @else if (checked()) {
                <svg data-checked fill="none" viewBox="0 0 14 11" xmlns="http://www.w3.org/2000/svg">
                    <path
                        d="M5.485 10.182a1 1 0 0 1-1.414 0l-3.03-3.03a1 1 0 0 1 0-1.415l.14-.141a1 1 0 0 1 1.415 0l2.182 2.182 6.626-6.627a1 1 0 0 1 1.414 0l.142.142a1 1 0 0 1 0 1.414l-7.475 7.475Z"
                        fill="currentColor" />
                </svg>
            }
        </span>
    `,
    styleUrl: './checkbox.scss',
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'checkbox',
    },
})
export class UICheckbox {
    /** Emits the new checked state (true or false) */
    checkedChange = output<boolean>();

    /** The [name](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/input#name) of the control. */
    readonly name = input.required<string>();

    /** The value of the field control. */
    readonly value = input<string | undefined>(undefined);

    /** The aria-label for the element. */
    readonly ariaLabel = input<string | undefined>(undefined);

    /** The id of the element. If not provided one will be generated. */
    readonly id = input<string>(uniqueId('checkbox'));

    /** Marks the checkbox as checked. @default false */
    readonly checked = input(false, { transform: booleanAttribute });

    /** If the checkbox is indeterminate. @default false */
    readonly indeterminate = input(false, { transform: booleanAttribute });

    /** Determines if the element is disabled. @default false */
    readonly disabled = input(false, { transform: booleanAttribute });

    /** Indicates that the element is in an invalid state. @default false */
    readonly invalid = input(false, { transform: booleanAttribute });

    /** Determines if the element is required. @default false */
    readonly required = input(false, { transform: booleanAttribute });

    /** The `aria-describedby` attribute for the checkbox. */
    readonly ariaDescribedBy = input<string | undefined>(undefined);

    /** The `aria-errormessage` attribute for the checkbox. */
    readonly ariaErrorMessage = input<string | undefined>(undefined);

    private readonly inputEl = viewChild.required<ElementRef<HTMLInputElement>>('input');

    constructor() {
        effect(() => {
            // Update indeterminate property on the native input
            const nativeInput: HTMLInputElement | null = this.inputEl().nativeElement;
            if (nativeInput) nativeInput.indeterminate = this.indeterminate();
        });
    }

    onInputChange() {
        this.checkedChange.emit(this.inputEl().nativeElement.checked);
    }
}

import { Component, ViewEncapsulation, input, Output, EventEmitter } from '@angular/core';
import { UICheckboxOption } from '../checkbox-option';

export interface CheckboxGroupOption {
    value: string;
    label: string;
    description?: string;
    checked?: boolean;
    disabled?: boolean;
    ariaLabel?: string;
}

@Component({
    selector: 'ui-checkbox-group',
    standalone: true,
    imports: [UICheckboxOption],
    encapsulation: ViewEncapsulation.None,
    template: `
        <div role="group">
            @if (selectAll()) {
                <ui-checkbox-option
                    name="select-all"
                    [label]="selectAllProps()?.label ?? selectAllLabel"
                    [ariaLabel]="selectAllProps()?.ariaLabel ?? selectAllLabel"
                    [description]="selectAllProps()?.description"
                    [checked]="allChecked()"
                    [indeterminate]="someChecked() && !allChecked()"
                    [disabled]="disabled()"
                    [invalid]="invalid()"
                    [value]="'all'"
                    (checkedChange)="onSelectAllChange($event)">
                </ui-checkbox-option>
            }
            @for (option of options(); track option.value) {
                <ui-checkbox-option
                    name="checkbox-group-option-{{ option.value }}"
                    [label]="option.label"
                    [value]="option.value"
                    [checked]="isChecked(option.value)"
                    [description]="option.description"
                    [disabled]="disabled() || option.disabled"
                    [invalid]="invalid()"
                    (checkedChange)="onOptionChange(option.value, $event)">
                </ui-checkbox-option>
            }
        </div>
    `,
})
export class UICheckboxGroup {
    @Output() valueChange = new EventEmitter<string[]>();
    readonly options = input<CheckboxGroupOption[]>([]);
    readonly value = input<string[]>([]);
    readonly selectAll = input<boolean>(false);
    readonly selectAllProps = input<Partial<CheckboxGroupOption> | undefined>(undefined);
    readonly disabled = input<boolean>(false);
    readonly invalid = input<boolean>(false);
    selectAllLabel = 'All';

    isChecked(optionValue: string): boolean {
        const value = this.value();
        return Array.isArray(value) ? value.includes(optionValue) : false;
    }
    availableOptions() {
        return this.options().filter((o) => !o.disabled);
    }
    allChecked() {
        const opts = this.availableOptions();
        const val = this.value();
        return opts.length > 0 && Array.isArray(val) && opts.every((o) => val.includes(o.value));
    }
    someChecked() {
        const opts = this.availableOptions();
        const val = this.value();
        return Array.isArray(val) && opts.some((o) => val.includes(o.value));
    }
    onSelectAllChange(checked: boolean) {
        const opts = this.availableOptions();
        this.valueChange.emit(checked ? opts.map((o) => o.value) : []);
    }
    onOptionChange(optionValue: string, checked: boolean) {
        const value = this.value();
        const current: string[] = Array.isArray(value) ? value : [];
        if (checked) {
            this.valueChange.emit([...current, optionValue]);
        } else {
            this.valueChange.emit(current.filter((v) => v !== optionValue));
        }
    }
}

import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { UICheckboxGroup } from './checkbox-group';

@Component({
    selector: 'ui-checkbox-group-example',
    standalone: true,
    imports: [CommonModule, UICheckboxGroup],
    template: `
        <h2>Checkbox Group Example</h2>
        <ui-checkbox-group [options]="options" [value]="selectedBasic" (valueChange)="onBasicValueChange($event)">
        </ui-checkbox-group>
        <div>Selected: {{ selectedBasic | json }}</div>

        <h2>Checkbox Group Example with Select All</h2>
        <ui-checkbox-group
            [options]="options"
            [value]="selectedAll"
            (valueChange)="onAllValueChange($event)"
            [selectAll]="true"
            [selectAllProps]="{ label: 'Select All', description: 'Select all options' }">
        </ui-checkbox-group>
        <div>Selected: {{ selectedAll | json }}</div>
    `,
})
export class UICheckboxGroupExample {
    options = [
        { label: 'Option 1', value: 'option1' },
        { label: 'Option 2', value: 'option2' },
        { label: 'Option 3', value: 'option3' },
    ];

    selectedBasic: string[] = [];
    selectedAll: string[] = [];

    onBasicValueChange(newValue: string[]) {
        this.selectedBasic = newValue;
    }

    onAllValueChange(newValue: string[]) {
        this.selectedAll = newValue;
    }
}

import { Component, input } from '@angular/core';
import { UICheckbox } from '../checkbox/checkbox';
import { UIListItem } from '../list-item';

/**
 * A control that allows users to choose one or more items from a list or turn a feature on or off.
 *
 * If only a checkbox button is needed, consider using the Checkbox component directly.
 *
 * @name CheckboxOption
 * @phase Utility
 */
@Component({
    selector: 'ui-checkbox-option',
    imports: [UICheckbox, UIListItem],
    standalone: true,
    template: `<ui-list-item
        [label]="label() || ''"
        [subText]="description()"
        [disabled]="disabled()"
        data-bspk="checkbox-option"
        [attr.aria-disabled]="disabled() ? true : null"
        as="label">
        <span data-leading>
            <ui-checkbox
                [id]="id()"
                [name]="name()"
                [value]="value()"
                [checked]="checked()"
                [indeterminate]="indeterminate()"
                [disabled]="disabled()"
                [required]="required()"
                [invalid]="invalid()"
                [ariaLabel]="ariaLabel()"
                (checkedChange)="checkedChange.emit($event)"
                #input>
            </ui-checkbox>
        </span>
    </ui-list-item>`,
    host: {
        'data-bspk': 'checkbox-option',
    },
})
export class UICheckboxOption extends UICheckbox {
    /** The label of the option. Also used as the aria-label of the control. */
    readonly label = input<string>();

    /** The description of the option. */
    readonly description = input<string | undefined>(undefined);

    /** The aria-label for the radio element. Combines label and description if both are present. */
    override readonly ariaLabel = input<string | undefined>(this.computedAriaLabel());

    computedAriaLabel(): string | undefined {
        return this.description() ? `${this.label()} - ${this.description()}` : this.label();
    }
}

