import { Component, ViewEncapsulation, input } from '@angular/core';
import { UIChip, ChipProps } from '../chip/chip';

export type Overflow = 'scroll' | 'wrap';

@Component({
    selector: 'ui-chip-group',
    standalone: true,
    imports: [UIChip],
    template: `@for (item of items(); track item) {
        <ui-chip
            [label]="item.label"
            [disabled]="item.disabled ?? false"
            [flat]="item.flat ?? false"
            [selected]="item.selected ?? false"
            [leadingIcon]="item.leadingIcon"
            [trailingIcon]="item.trailingIcon"
            [trailingBadge]="item.trailingBadge"
            (click)="item.click?.($event)" />
    }`,
    styleUrl: './chip-group.scss',
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'chip-group',
        '[attr.data-scroll]': "overflow() === 'scroll' ? true : null",
    },
})
export class UIChipGroup {
    /** To allow chips to scroll. If set to false chips will wrap. */
    readonly overflow = input<Overflow>('wrap');

    /** Only Chip components should be used as children. */
    readonly items = input<ChipProps[]>();
}

import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { sendSnackbar } from '../../utils/send-snackbar';
import { ChipProps } from '../chip';
import { IconAccessible } from '../icons/accessible';
import { IconAdd } from '../icons/add';
import { IconBakeryDining } from '../icons/bakery-dining';
import { IconBlock } from '../icons/block';
import { IconCloud } from '../icons/cloud';
import { IconDarkMode } from '../icons/dark-mode';
import { IconEgg } from '../icons/egg';
import { IconGarage } from '../icons/garage';
import { IconIcecream } from '../icons/icecream';
import { IconLightMode } from '../icons/light-mode';
import { IconModeCool } from '../icons/mode-cool';
import { IconPets } from '../icons/pets';
import { IconPlayPause } from '../icons/play-pause';
import { IconRemove } from '../icons/remove';
import { IconReplay } from '../icons/replay';
import { UIChipGroup } from './chip-group';

@Component({
    selector: 'ui-chip-group-example',
    standalone: true,
    imports: [CommonModule, UIChipGroup],
    template: `
        <h2>Chip Group</h2>

        <h3>Basic</h3>
        <div style="width: 500px">
            <ui-chip-group [items]="basicChips" />
        </div>

        <h3>Scroll</h3>
        <div style="width: 500px">
            <ui-chip-group
                overflow="scroll"
                [items]="[
                    { label: 'Chip 1', trailingIcon: iconRemove },
                    { label: 'Chip 2', trailingIcon: iconAdd },
                    { label: 'Chip 3', trailingBadge: { count: 2 } },
                    { label: 'Chip 4', trailingIcon: iconReplay },
                    { label: 'Chip 5 selected = true', trailingIcon: iconPlayPause, selected: true },
                    { label: 'Chip 6 disabled = true', trailingIcon: iconBlock, disabled: true },
                ]">
            </ui-chip-group>
        </div>

        <h3>Scroll & Flat</h3>
        <div style="width: 500px">
            <ui-chip-group
                overflow="scroll"
                [items]="[
                    { label: 'Chip 1', leadingIcon: iconModeCool, flat: true },
                    { label: 'Chip 2', leadingIcon: iconLightMode, flat: true },
                    { label: 'Chip 3', leadingIcon: iconCloud, trailingBadge: { count: 2 }, flat: true },
                    { label: 'Chip 4', leadingIcon: iconAdd, flat: true },
                    { label: 'Chip 5 selected = true', leadingIcon: iconDarkMode, selected: true, flat: true },
                    { label: 'Chip 6 disabled = true', leadingIcon: iconEgg, disabled: true, flat: true },
                ]">
            </ui-chip-group>
        </div>
    `,
})
export class UIChipGroupExample {
    protected readonly basicChips: ChipProps[] = [
        {
            label: 'Chip 1',
            leadingIcon: IconIcecream,
            click: () => sendSnackbar('Chip 1 clicked'),
        },
        { label: 'Chip 2', leadingIcon: IconAccessible },
        { label: 'Chip 3', leadingIcon: IconAdd, trailingBadge: { count: 2 } },
        { label: 'Chip 4', leadingIcon: IconGarage },
        { label: 'Chip 5 selected = true', leadingIcon: IconBakeryDining, selected: true },
        { label: 'Chip 6 disabled = true', leadingIcon: IconPets, disabled: true },
    ];

    protected readonly iconIcecream = IconIcecream;
    protected readonly iconAccessible = IconAccessible;
    protected readonly iconAdd = IconAdd;
    protected readonly iconGarage = IconGarage;
    protected readonly iconBakeryDining = IconBakeryDining;
    protected readonly iconPets = IconPets;
    protected readonly iconRemove = IconRemove;
    protected readonly iconReplay = IconReplay;
    protected readonly iconPlayPause = IconPlayPause;
    protected readonly iconBlock = IconBlock;
    protected readonly iconModeCool = IconModeCool;
    protected readonly iconLightMode = IconLightMode;
    protected readonly iconCloud = IconCloud;
    protected readonly iconDarkMode = IconDarkMode;
    protected readonly iconEgg = IconEgg;
}

import {
    Component,
    ElementRef,
    EventEmitter,
    Output,
    ViewEncapsulation,
    input,
    signal,
    OnChanges,
    OnDestroy,
    viewChild,
} from '@angular/core';
import { UIFocusTrapDirective } from '../focus-trap';
import { UIPortalDirective } from '../portal';
import { UIScrim } from '../scrim/scrim';

export type Placement = 'bottom' | 'center' | 'left' | 'right' | 'top';

/**
 * Dialogs display important information that users need to acknowledge. They appear over the interface and may block
 * further interactions until an action is selected.
 *
 * The Modal component is a higher-level component built on top of Dialog that includes standard dialog UI and behavior.
 *
 * Also known as: Tray, Drawer, Flyout, Sheet
 *
 * @example
 *     <ui-dialog [open]="open" (onClose)="open=false">
 *     <div style="padding: var(--spacing-sizing-04)">
 *     <h4>Dialog Title</h4>
 *     <p>This is the content of the dialog.</p>
 *     </div>
 *     </ui-dialog>
 *
 * @name Dialog
 * @phase Dev
 */
@Component({
    selector: 'ui-dialog',
    standalone: true,
    imports: [UIPortalDirective, UIScrim, UIFocusTrapDirective],
    template: `
        @if (open()) {
            <div
                [ui-portal]="container()"
                [attr.data-bspk]="'dialog'"
                [attr.data-bspk-owner]="owner() || null"
                [attr.data-contained]="container() ? true : null"
                [attr.data-placement]="placement()"
                [attr.id]="id() || null"
                [attr.aria-label]="ariaLabel() || null"
                [attr.aria-description]="ariaDescription() || null"
                role="presentation">
                <div
                    data-dialog-box
                    [ui-focus-trap]="!disableFocusTrap()"
                    [attr.data-width-full]="widthFull()"
                    #box
                    tabindex="-1">
                    <ng-content></ng-content>
                </div>
            </div>
            <ui-scrim
                [ui-portal]="container()"
                [contained]="container() ? true : undefined"
                owner="dialog"
                [visible]="showScrim() !== false"
                (click)="handleClose()" />
        }
    `,
    styleUrl: './dialog.scss',
    encapsulation: ViewEncapsulation.None,
    host: {
        style: 'display: contents;',
    },
})
export class UIDialog implements OnChanges, OnDestroy {
    /** Function to call when the dialog is closed. */
    @Output() onClose = new EventEmitter<void>();

    readonly box = viewChild('box', { read: ElementRef });

    /** A ref to the dialog element. */
    readonly innerRef = input<(el: HTMLDivElement | null) => void>();

    /** If the dialog should appear. */
    readonly open = input<boolean>(false);

    /** The placement of the dialog on the screen. */
    readonly placement = input<Placement>('center');

    /** Whether the dialog should have a scrim behind it. */
    readonly showScrim = input<boolean>(true);

    /** If the dialog should take the full width of the screen. */
    readonly widthFull = input<boolean>(false);

    /** Owner tag for theming/analytics parity. */
    readonly owner = input<string | undefined>(undefined);

    /** Element id. */
    readonly id = input<string | undefined>(undefined);

    /** Portal container element to contain dialog within. */
    readonly container = input<HTMLElement | undefined>(undefined);

    /** Disable focus trap (not implemented here; Angular users can manage focus as needed). */
    readonly disableFocusTrap = input<boolean>(false);

    /** Accessible label for the dialog container. */
    readonly ariaLabel = input<string | undefined>(undefined);

    /** Accessible description for the dialog container. */
    readonly ariaDescription = input<string | undefined>(undefined);

    private readonly keydownBound = signal(false);

    ngOnChanges() {
        const isOpen = this.open();
        // Lock page scroll when open (like React)
        document.documentElement.style.overflow = isOpen ? 'hidden' : '';
        if (isOpen && !this.keydownBound()) {
            window.addEventListener('keydown', this._onKeydown);
            this.keydownBound.set(true);
        } else if (!isOpen && this.keydownBound()) {
            window.removeEventListener('keydown', this._onKeydown);
            this.keydownBound.set(false);
        }
    }

    ngOnDestroy() {
        document.documentElement.style.overflow = '';
        if (this.keydownBound()) {
            window.removeEventListener('keydown', this._onKeydown);
            this.keydownBound.set(false);
        }
    }

    handleClose() {
        this.onClose.emit();
    }

    private _onKeydown = (e: KeyboardEvent) => {
        if (this.open() && e.key === 'Escape') {
            this.handleClose();
        }
    };
}

