import { Component, ViewEncapsulation, input } from '@angular/core';
import { CommonProps, AsInputSignal } from '../../types/common';

export type MenuProps = CommonProps<'ariaLabel' | 'ariaRole' | 'id' | 'owner'> & {
    /**
     * The width of the menu.
     *
     * @type HTMLElement.style.width
     * @exampleType string
     */
    width?: HTMLElement['style']['width'];
};

/**
 * A container housing a simple list of options presented to the customer to select one option at a time.
 *
 * @example
 *     <ui-menu>
 *     <ui-list-item label="List Item"></ui-list-item>
 *     <ui-list-item label="List Item"></ui-list-item>
 *     <ui-list-item label="List Item"></ui-list-item>
 *     </ui-menu>
 *
 * @name Menu
 * @phase Dev
 */
@Component({
    selector: 'ui-menu',
    standalone: true,
    // Host-based rendering: base is a div, so attach all attributes to host
    template: `<ng-content></ng-content>`,
    styleUrl: './menu.scss',
    encapsulation: ViewEncapsulation.None,
    host: {
        '[attr.aria-label]': 'ariaLabel() || null',
        '[attr.data-bspk-owner]': 'owner() || null',
        'data-bspk-utility': 'menu',
        '[attr.id]': 'id() || null',
        '[attr.role]': 'ariaRole() || null',
        '[style.width]': 'width() || null',
        tabindex: '-1',
    },
})
export class UIMenu implements AsInputSignal<MenuProps> {
    readonly ariaLabel = input<MenuProps['ariaLabel']>(undefined);
    readonly width = input<MenuProps['width']>(undefined);
    readonly owner = input<MenuProps['owner']>(undefined);
    readonly id = input<MenuProps['id']>(undefined);
    readonly ariaRole = input<MenuProps['ariaRole']>(undefined);
}

import { Component, EventEmitter, Output, ViewEncapsulation, input, computed, inject } from '@angular/core';
import { ButtonSize } from '../../types/common';
import { UIBreakpointService } from '../../utils/breakpoint.service';
import { UIButton, ButtonWidth } from '../button/button';
import { UIDialog } from '../dialog/dialog';
import { IconClose } from '../icons/close';
import { UIMatchParentHeightDirective } from '../match-parent-height';

export interface ModalCallToAction {
    label: string;
    onClick: () => void;
    destructive?: boolean;
}

export type ButtonFormat = 'horizontal' | 'vertical';

/**
 * Modals display important information that users need to acknowledge. They appear over the interface and block further
 * interactions until an action is selected. Modal is a wrapper around the Dialog component that provides a header and
 * footer for the dialog.
 *
 * @example
 *     <ui-button label="Open Modal" (onClick)="open = true"></ui-button>
 *     <ui-modal
 *     description="Example description"
 *     header="Example header"
 *     (onClose)="open = false"
 *     [open]="open">
 *     Example Modal
 *     </ui-modal>
 *
 * @name Modal
 * @phase Dev
 */
@Component({
    selector: 'ui-modal',
    standalone: true,
    imports: [UIDialog, UIButton, UIMatchParentHeightDirective],
    template: `
        @if (open()) {
            <ui-dialog
                [open]="open()"
                (onClose)="onClose.emit()"
                [container]="container()"
                [disableFocusTrap]="disableFocusTrap()"
                [id]="id() || undefined"
                [owner]="owner() || undefined"
                [ariaLabel]="header()"
                [ariaDescription]="description()"
                placement="center"
                [showScrim]="true">
                <div data-bspk="modal" #innerRef ui-match-parent-height>
                    <div data-modal-header>
                        <div data-modal-title>{{ header() }}</div>
                        <ui-button
                            label="close"
                            variant="tertiary"
                            [size]="buttonSize()"
                            (onClick)="onClose.emit()"
                            [icon]="iconClose"
                            [iconOnly]="true"></ui-button>
                    </div>
                    <div data-modal-main>
                        <ng-content></ng-content>
                    </div>
                    @if (hasFooterButtons()) {
                        <div data-modal-footer [attr.data-button-format]="buttonFormat()">
                            @if (callToAction()) {
                                <ui-button
                                    [label]="callToAction()!.label"
                                    [variant]="'primary'"
                                    [size]="buttonSize()"
                                    [width]="buttonWidth()"
                                    [destructive]="callToAction()!.destructive ?? false"
                                    (onClick)="callToAction()!.onClick()" />
                            }
                            @if (callToAction() && cancelButton()) {
                                <ui-button
                                    [label]="cancelLabel()"
                                    [variant]="'tertiary'"
                                    [size]="buttonSize()"
                                    [width]="buttonWidth()"
                                    (onClick)="onClose.emit()" />
                            }
                        </div>
                    }
                </div>
            </ui-dialog>
        }
    `,
    styleUrl: './modal.scss',
    encapsulation: ViewEncapsulation.None,
})
export class UIModal {
    /** Emits when modal requests to close. */
    @Output() onClose = new EventEmitter<void>();

    iconClose = IconClose;

    /** Modal header. */
    readonly header = input.required<string>();

    /** Modal description. Used for the aria-description attribute. */
    readonly description = input.required<string>();

    /** A ref to the modal element. */
    readonly innerRef = input<(el: HTMLDivElement | null) => void>();

    /** Whether to show the cancel button in the footer. Providing a string sets its label. */
    readonly cancelButton = input<boolean | string>(false);

    /** The call to action button to display in the footer of the modal. */
    readonly callToAction = input<ModalCallToAction | undefined>(undefined);

    /** The format of the buttons in the footer. Vertical only applies on <=640px. */
    readonly buttonFormat = input<ButtonFormat>('horizontal');

    // Dialog-proxy inputs
    readonly open = input<boolean>(false);
    readonly id = input<string | undefined>(undefined);
    readonly owner = input<string | undefined>(undefined);
    readonly container = input<HTMLElement | undefined>(undefined);
    readonly disableFocusTrap = input<boolean>(false);

    // Defaults: Angular app likely desktop; keep small; width depends on format
    readonly buttonSize = computed<ButtonSize>(() => (this.isMobile() ? 'medium' : 'small'));
    readonly buttonWidth = computed<ButtonWidth>(() => (this.buttonFormat() === 'vertical' ? 'fill' : 'hug'));

    readonly cancelLabel = computed<string>(() =>
        typeof this.cancelButton() === 'string' ? (this.cancelButton() as string) : 'Cancel',
    );

    readonly hasFooterButtons = computed<boolean>(() => !!this.callToAction());

    breakpointService = inject(UIBreakpointService);

    readonly isMobile = computed(
        () =>
            this.breakpointService.currentBreakpointSignal() === 'small' ||
            this.breakpointService.currentBreakpointSignal() === 'medium',
    );
}

import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { sendSnackbar } from '../../utils/send-snackbar';
import { UIButton } from '../button';
import { UIOutsideClickDirective } from './outside-click.directive';

@Component({
    selector: 'ui-outside-click-example',
    standalone: true,
    imports: [CommonModule, UIOutsideClickDirective, UIButton],
    template: `
        <h2>Outside Click (Directive)</h2>

        <h3>Single Button with Outside Click Detection</h3>
        <ui-button
            label="Click outside me!"
            [ui-outside-click]="{ callback: outsideClick('1') }"
            (click)="insideClick('1')()">
        </ui-button>

        <h3>Multiple Elements with Outside Click Detection</h3>
        <div
            style="border: 1px solid var(--stroke-neutral-base); padding: 16px; display: inline-block"
            [ui-outside-click]="{ callback: outsideClick('2') }">
            <p>Clicking inside this box will not trigger the outside click callback.</p>
            <ui-button label="Inside Button" (click)="insideClick('2')()"></ui-button>
        </div>
    `,
})
export class UIOutsideClickExample {
    outsideClick(num: string) {
        return () => {
            sendSnackbar(`Outside click (${num}) detected!`);
        };
    }

    insideClick(num: string) {
        return () => sendSnackbar(`Inside click (${num}) detected!`);
    }
}

import { CommonModule } from '@angular/common';
import { Component, Output, EventEmitter, OnChanges, SimpleChanges, ViewEncapsulation, input } from '@angular/core';
import { sendAriaLiveMessage } from '../../utils/send-aria-live-message';
import { UIButton } from '../button/button';
import { IconChevronLeft, IconChevronRight } from '../icons';
import { UIInput } from '../input';

// After this point the manual input renders. With equal or fewer pages
// the individual page buttons render instead (matches React implementation).
const INPUT_TYPE_THRESHOLD = 7;

/**
 * A navigation component that allows customers to move between a range of listed content within a page into smaller
 * multiple micro pages.
 *
 * @example
 *     ```html
 *     <ui-pagination [value]="currentPage" [numPages]="10" (onChange)="currentPage = $event"></ui-pagination>
 *     ```;
 *
 * @name Pagination
 * @phase Dev
 */
@Component({
    selector: 'ui-pagination',
    standalone: true,
    imports: [CommonModule, UIButton, UIInput],
    template: `@if (numPages() > 1) {
        <ng-container>
            <ui-button
                [disabled]="!inBounds(value() - 1)"
                [icon]="ChevronLeft"
                [iconOnly]="true"
                [label]="value() === 1 ? 'First page' : 'Previous page (' + (value() - 1) + ')'"
                (onClick)="previousPage()"
                size="small"
                variant="tertiary"
                owner="pagination"></ui-button>

            @if (numPages() > INPUT_TYPE_THRESHOLD) {
                <ng-container>
                    <form data-input-form (submit)="submitManual($event)">
                        <ui-input
                            label="Page number"
                            [type]="'number'"
                            [showClearButton]="false"
                            [value]="inputValue"
                            (valueChange)="inputValue = $event!"
                            (blur)="submitManual()"
                            name="page-number" />
                        <span>of {{ numPages() }}</span>
                    </form>
                </ng-container>
            } @else {
                <ng-container *ngTemplateOutlet="pageButtons"></ng-container>
            }

            <ng-template #pageButtons>
                @for (page of pages(); track $index) {
                    <ui-button
                        [attr.aria-label]="'Page ' + page"
                        [label]="page.toString()"
                        (onClick)="emit(page)"
                        size="small"
                        [variant]="value() === page ? 'primary' : 'tertiary'"
                        owner="pagination"></ui-button>
                }
            </ng-template>

            <ui-button
                [disabled]="!inBounds(value() + 1)"
                [icon]="ChevronRight"
                [iconOnly]="true"
                [label]="value() === numPages() ? 'Last page' : 'Next page (' + (value() + 1) + ')'"
                (onClick)="nextPage()"
                size="small"
                variant="tertiary"
                owner="pagination"></ui-button>
        </ng-container>
    }`,
    styleUrls: ['./pagination.scss'],
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'pagination',
        role: 'group',
    },
})
export class UIPagination implements OnChanges {
    /** Called when the page changes. */
    @Output() onChange = new EventEmitter<number>();

    /**
     * The number of pages to display in the pagination component.
     *
     * If there is only one page, the component will not render.
     *
     * @default 2
     */
    readonly numPages = input(2);
    /**
     * The current (1-based) page number.
     *
     * @default 1
     */
    readonly value = input(1);

    // Internal string representation for the input field when large page counts.
    inputValue = '1';

    readonly INPUT_TYPE_THRESHOLD = INPUT_TYPE_THRESHOLD;

    ChevronRight = IconChevronRight;
    ChevronLeft = IconChevronLeft;

    ngOnChanges(changes: SimpleChanges) {
        if (changes['value']) {
            this.inputValue = String(this.value());
        }
    }

    emit(page: number) {
        const next = this.clamp(page);
        this.onChange.emit(next);
        sendAriaLiveMessage(`Page ${page} of ${this.numPages()}`);
    }

    previousPage() {
        if (this.value() > 1) this.emit(this.value() - 1);
    }

    nextPage() {
        if (this.value() < this.numPages()) this.emit(this.value() + 1);
    }

    submitManual(event?: Event) {
        if (event) event.preventDefault();
        const parsedValue = parseInt(this.inputValue || '', 10);
        if (isNaN(parsedValue)) {
            this.inputValue = String(this.value());
            return;
        }
        const next = this.clamp(parsedValue);
        this.emit(next);
        if (next !== parsedValue) this.inputValue = String(next);
    }

    inBounds(n: number): boolean {
        return n >= 1 && n <= this.numPages();
    }

    pages(): number[] {
        return Array.from({ length: this.numPages() }, (_, i) => i + 1);
    }

    label(): string {
        return `Go to page ${this.value()}`;
    }

    private clamp(page: number): number {
        if (page < 1) return 1;
        if (page > this.numPages()) return this.numPages();
        return page;
    }
}

