import { CommonModule } from '@angular/common';
import { Component, model } from '@angular/core';
import { sendSnackbar } from '../../utils/send-snackbar';
import { UIPagination } from './pagination';

@Component({
    selector: 'ui-pagination-example',
    standalone: true,
    imports: [CommonModule, UIPagination],
    template: `
        <h2>Pagination</h2>

        <p>See working example on the table page: <a href="/table">Table</a></p>

        <h3>Pagination with Input</h3>
        <ui-pagination [value]="values()[0]" [numPages]="50" (onChange)="handleChange(0, $event)" />

        <h3>Pagination with Buttons</h3>
        <ui-pagination [value]="values()[1]" [numPages]="7" (onChange)="handleChange(1, $event)" />

        <h3>Hidden when only one page</h3>
        <ui-pagination [value]="values()[2]" [numPages]="1" (onChange)="handleChange(2, $event)" />
        <p>This pagination component is hidden because there is only one page.</p>
    `,
})
export class UIPaginationExample {
    protected readonly values = model([1, 5, 1]);

    protected handleChange(exampleIndex: number, num: number) {
        sendSnackbar(`Pagination changed to page ${num}`);
        const values = this.values();
        values[exampleIndex] = num;
        this.values.set(values);

        // No operation for example
    }
}

import { Component, Output, EventEmitter, ViewEncapsulation, input, booleanAttribute } from '@angular/core';
import { uniqueId } from '../../utils/random';

/**
 * A round control that allows user to choose one option from a set. This is the base element and if used directly you
 * must wrap it with a label. This will more often be used in the RadioOption or RadioGroup component.
 *
 * <ui-radio name="example" [value]="'option1'" (checkedChange)="selected = $event ? 'option1' : selected"
 * [disabled]="false" [invalid]="false" [required]="true" aria-label="Option 1"
 *
 * > </ui-radio>
 *
 * @name Radio
 * @phase Utility
 */

@Component({
    selector: 'ui-radio',
    standalone: true,
    imports: [],
    template: `<input
            [attr.name]="name()"
            [attr.id]="id()"
            [attr.aria-label]="ariaLabel()"
            type="radio"
            [attr.value]="value()"
            [checked]="checked()"
            [attr.checked]="checked() ? '' : null"
            [required]="required()"
            [attr.data-invalid]="invalid() ? true : null"
            [disabled]="disabled()"
            (change)="onInputChange($event)" />
        <span aria-hidden="true"></span>`,
    styleUrl: './radio.scss',
    encapsulation: ViewEncapsulation.None,
    host: {
        'data-bspk': 'radio',
    },
})
export class UIRadio {
    /** Emits the new checked state (true or false) */
    @Output() checkedChange = new EventEmitter<boolean>();

    /**
     * The function to call when the radio is checked.
     *
     * @required
     */
    readonly change = input<((event: Event) => void) | undefined>(undefined);

    /**
     * The [name](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/input#name) of the control.
     *
     * @required
     */
    readonly name = input.required<string | undefined>();

    /**
     * The value of the field control.
     *
     * @required
     */
    readonly value = input.required<string | undefined>();

    /**
     * The aria-label for the element.
     *
     * This is used to provide an accessible name for the element when a visible label is not present.
     *
     * Ensure this is provided when using the element in isolation to maintain accessibility.
     */
    readonly ariaLabel = input<string | undefined>(undefined);

    /**
     * Marks the radio as checked.
     *
     * @default false
     */
    readonly checked = input(false, { transform: booleanAttribute });

    /**
     * Determines if the element is [disabled](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/disabled).
     *
     * @default false
     */
    readonly disabled = input(false, { transform: booleanAttribute });

    /** The id of the element. If not provided one will be generated. */
    readonly id = input<string>(uniqueId('radio'));

    /**
     * Indicates that the element is in an invalid state and displays the error theme.
     *
     * If set to true, an accompanying error message should be provided.
     *
     * @default false
     */
    readonly invalid = input(false, { transform: booleanAttribute });

    /**
     * Determines if the element is [required](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/required).
     *
     * @default false
     */
    readonly required = input(false, { transform: booleanAttribute });

    onInputChange(event: Event) {
        const inputElement = event.target as HTMLInputElement;
        this.checkedChange.emit(inputElement.checked);
    }
}

import { Component, ViewEncapsulation, input, Output, EventEmitter } from '@angular/core';
import { UIRadioOption } from '../radio-option/radio-option';

export interface RadioGroupOption {
    value: string;
    label: string;
    description?: string;
    checked?: boolean;
    disabled?: boolean;
}

/**
 * A group of radios that allows users to choose one or more items from a list or turn an feature on or off.
 *
 * For a more complete example with field usage, see the RadioGroupField component.
 *
 * @example
 *     <ui-radio-group
 *     [options]="options"
 *     [value]="selectedValue"
 *     (valueChange)="onValueChange($event)">
 *     </ui-radio-group>
 *
 * @name RadioGroup
 * @phase Dev
 */

@Component({
    selector: 'ui-radio-group',
    standalone: true,
    imports: [UIRadioOption],
    template: `
        <div
            [attr.aria-describedby]="ariaDescribedBy() || null"
            [attr.aria-errormessage]="ariaErrorMessage() || null"
            [attr.id]="id()"
            role="radiogroup"
            data-bspk="radio-group"
            style="display: flex; flex-direction: column; gap: 0; max-width: 100%; --list-item-height: auto;">
            @for (option of options(); track option.value) {
                <ui-radio-option
                    [label]="option.label"
                    [description]="option.description"
                    [name]="name()"
                    [value]="option.value"
                    [checked]="value() === option.value"
                    [disabled]="disabled() || option.disabled"
                    [required]="required()"
                    [invalid]="invalid()"
                    (checkedChange)="onRadioChange(option.value, $event)">
                </ui-radio-option>
            }
        </div>
    `,
    host: {
        'data-bspk': 'radio-group',
        'style.display': 'flex',
        'style.flex-direction': 'column',
        'style.gap': '0',
        'style.max-width': '100%',
        'style.--list-item-height': 'auto',
    },
    encapsulation: ViewEncapsulation.None,
})
export class UIRadioGroup extends UIRadioOption {
    @Output() valueChange = new EventEmitter<string>();
    /** Radio group options */
    readonly options = input<RadioGroupOption[]>([]);
    /**
     * The `aria-describedby` attribute for the radio group.
     *
     * This should be set to the ID of an element that contains additional descriptive text for the group. When
     * provided, assistive technologies will announce the referenced description to users, improving accessibility.
     *
     * @see https://developer.mozilla.org/en-US/docs/Web/Accessibility/ARIA/Attributes/aria-describedby
     */
    readonly ariaDescribedBy = input<string | null>(null);
    /**
     * The `aria-errormessage` attribute for the radio group.
     *
     * This should be set to the ID of an element that contains an error message describing the validation error for the
     * group. When provided, assistive technologies will announce the referenced error message when the group is
     * invalid.
     *
     * @see https://developer.mozilla.org/en-US/docs/Web/Accessibility/ARIA/Attributes/aria-errormessage
     */
    readonly ariaErrorMessage = input<string | null>(null);
    /** Handles changes to the radio button selection. */
    onRadioChange(value: string, checked: boolean) {
        if (checked) {
            this.valueChange.emit(value);
        }
    }
}

import { Component, input } from '@angular/core';
import { UIListItem } from '../list-item';
import { UIRadio } from '../radio/radio';

/**
 * A control that allows users to choose one or more items from a list or turn an feature on or off.
 *
 * If only a radio button is needed, consider using the Radio component directly.
 *
 * @name RadioOption
 * @phase Utility
 */
@Component({
    selector: 'ui-radio-option',
    imports: [UIRadio, UIListItem],
    template: `<ui-list-item
        [label]="label() || ''"
        [subText]="description()"
        [disabled]="disabled()"
        data-bspk="radio-option"
        [attr.aria-disabled]="disabled() ? true : null"
        as="label">
        <span data-leading>
            <ui-radio
                [id]="id()"
                [name]="name()"
                [value]="value()"
                [checked]="checked()"
                [disabled]="disabled()"
                [required]="required()"
                [invalid]="invalid()"
                [ariaLabel]="ariaLabel()"
                (checkedChange)="checkedChange.emit($event)">
            </ui-radio>
        </span>
    </ui-list-item>`,
    host: {
        'data-bspk': 'radio-option',
    },
})
export class UIRadioOption extends UIRadio {
    /** The label of the option. Also used as the aria-label of the control. */
    readonly label = input<string>();

    /** The description of the option. */
    readonly description = input<string | undefined>(undefined);
    /** The aria-label for the radio element. Combines label and description if both are present. */

    override readonly ariaLabel = input<string | undefined>(this.computedAriaLabel());

    computedAriaLabel(): string | undefined {
        return this.description() ? `${this.label()} - ${this.description()}` : this.label();
    }
}

