        { value: '3', label: 'Option 3' },
        { value: '4', label: 'Option 4' },
    ];

    iconOptions = [
        { value: '1', label: 'Home', icon: IconHome },
        { value: '2', label: 'Search', icon: IconSearch },
        { value: '3', label: 'Profile', icon: IconAccountCircle },
        { value: '4', label: 'Option 4' },
    ] as any;

    optionsWithDisabled = [
        { value: '1', label: 'Option 1' },
