        [label]="label() || ''"
        [subText]="description()"
        [disabled]="disabled()"
        data-bspk="switch-option"
        [attr.aria-disabled]="disabled() ? true : null"
        as="label">
        <span data-leading>
            <ui-switch
                [id]="id()"
                [name]="name()"
                [value]="value()"
                [checked]="checked()"
                [disabled]="disabled()"
                [ariaLabel]="ariaLabel()"
                (checkedChange)="checkedChange.emit($event)">
            </ui-switch
        ></span>
    </ui-list-item>`,
    host: {
        'data-bspk': 'switch-option',
    },
})
export class UISwitchOption extends UISwitch {
    /** The label of the option. Also used as the aria-label of the control. */
    readonly label = input<string>();

    /** The description of the option. */
    readonly description = input<string | undefined>(undefined);

    /** The aria-label for the switch element. Combines label and description if both are present. */
    override readonly ariaLabel = input<string | undefined>(this.computedAriaLabel());

    computedAriaLabel(): string | undefined {
        return this.description() ? `${this.label()} - ${this.description()}` : this.label();
    }
}

import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { sendSnackbar } from '../../utils/send-snackbar';
import { UISwitchOption } from './switch-option';

@Component({
    selector: 'ui-switch-option-example',
    standalone: true,
    imports: [CommonModule, UISwitchOption],
    template: `
        <h2>Switch Option</h2>
        @for (ex of examples; track $index) {
            <h3>{{ ex.label }}</h3>
            <ui-switch-option
                [label]="'This is ' + ex.label"
                [description]="'This is description for ' + ex.label"
                [id]="ex.id"
                [name]="ex.name || 'example-switch-group'"
                [value]="ex.value || ex.label || 'option' + $index"
                [checked]="ex.checked"
                [disabled]="ex.disabled"
                [ariaLabel]="ex.ariaLabel"
                (checkedChange)="onCheckedChange($index, $event)" />
            <div>Demo checked: {{ ex.checked }}</div>
        }
    `,
})
export class UISwitchOptionExample {
    examples: (Partial<{
        label: string;
        description: string;
        name: string;
        value: string;
        checked: boolean;
        disabled: boolean;
        ariaLabel: string;
    }> & { id: string })[] = [
        { label: 'basic' },
        { label: 'checked = true', checked: true },
        { label: 'checked = false', checked: false },
        { label: 'checked = true, disabled = true', checked: true, disabled: true },
        { label: 'disabled = true', disabled: true },
        { label: 'checked = false, disabled = true ', disabled: true, checked: false },
    ].map((config, idx) => ({
        id: `switch-option-example-${idx}`,
        name: `example-switch-group-${idx}`,
        value: `option-${idx}`,
        ...config,
    }));

    onCheckedChange(idx: number, checked: boolean) {
        this.examples[idx].checked = checked;
        sendSnackbar(`Switch ${idx} checked: ${checked}`);
    }
}

