[data-bspk='tag'] {
    display: inline-flex;
    align-items: center;
    position: relative;
    padding: 0 var(--spacing-sizing-03);
    border-radius: var(--radius-sm);
    color: var(--foreground) !important;
    background: var(--background);
    font: unset;
    max-width: 200px;
    flex-shrink: 0;
    flex-grow: 0;
    width: fit-content;

    &[data-variant='pill'] {
        border-radius: var(--radius-full);
    }

    &[data-variant='corner-wrap'] {
        border-bottom-right-radius: 0;
    }

    &[data-size='small'] {
        font: var(--labels-small);
        height: var(--spacing-sizing-08);
    }

    &[data-size='x-small'] {
        font: var(--labels-x-small);
        height: var(--spacing-sizing-06);
    }

    &[data-color='white'] {
        box-shadow: var(--drop-shadow-south);
    }

    [data-triangle] {
        position: absolute;
        bottom: -12px;
        right: 0;
        width: 0;
        height: 0;
        border-style: solid;
        border-width: 12px 12px 0 0;
        border-color: var(--foreground) transparent transparent transparent;
        transform: rotate(0deg);
    }
}

